import React, { useState } from 'react';
import '../asset/css/seniorForm.css';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ResumeTemplate from './ResumeTemplate';

const SeniorJobApplicationForm = () => {
  const [formData, setFormData] = useState({
    // 기본 정보
    name: '',
    title: '',
    residentNumber1: '',
    residentNumber2: '',
    address: '',
    homePhone: '',
    mobilePhone: '',
    familyPhone: '',
    postcode: '',
    detailAddress: '',
    photo: null,
    
    // 최종학력
    education: '',
    
    // 세대구성 형태
    householdType: '',
    
    // 동거가족
    familyMembers: '',
    spouse: '',
    children: '',
    grandchildren: '',
    
    // 일자리사업 참여경력
    workExperience: {
      isNew: false,
      years: []
    },
    
    // 기초생활수급권자
    basicLivingRecipient: '',
    
    // 경제상태
    economicStatus: '',
    
    // 주거형태
    housingType: '',
    
    // 건강상태
    healthStatus: '',
    
    // 참여희망사업
    desiredPrograms: {
      first: '',
      second: '',
      third: ''
    },
    
    // 신청동기
    applicationMotives: [],
    
    // 자원봉사 참여
    volunteerWork: {
      participated: false,
      healthInsurance: ''
    },
    
    // 기초노령연금
    basicPensionStatus: {
      receiving: false,
      amount: ''
    },
    
    // 장기요양
    longTermCare: {
      status: false
    }
  });

  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [photoPreview, setPhotoPreview] = useState(null);

  const educationOptions = [
    { value: '무학', label: '무학', icon: '📚' },
    { value: '초졸', label: '초등학교 졸업', icon: '🎓' },
    { value: '중졸', label: '중학교 졸업', icon: '🎓' },
    { value: '고졸', label: '고등학교 졸업', icon: '🎓' },
    { value: '전문대졸', label: '전문대학 졸업', icon: '🎓' },
    { value: '대졸이상', label: '대학교 졸업 이상', icon: '🎓' }
  ];

  const householdOptions = [
    { value: '독신', label: '혼자 살고 있어요', icon: '🏠' },
    { value: '노인부부', label: '배우자와 함께 살고 있어요', icon: '👫' },
    { value: '가족동거(노부모,손자녀,장애인)', label: '부모님/손주/장애인 가족과 함께 살고 있어요', icon: '👨‍👩‍👧‍👦' },
    { value: '가족동거(경제능력가족)', label: '경제활동을 하는 가족과 함께 살고 있어요', icon: '💼' },
    { value: '기타', label: '기타', icon: '📝' }
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handlePhotoCapture = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, photo: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const nextStep = () => {
    if (validateCurrentStep()) {
      setStep(prev => Math.min(prev + 1, totalSteps));
    } else {
      alert('필수 항목을 입력해주세요.');
    }
  };

  const prevStep = () => {
    setStep(prev => Math.max(prev - 1, 1));
  };

  const validateCurrentStep = () => {
    switch(step) {
      case 1:
        return formData.name && 
               formData.residentNumber1 && 
               formData.residentNumber2 && 
               formData.address && 
               formData.mobilePhone;
      case 2:
        return formData.education && formData.householdType;
      case 3:
        return formData.basicLivingRecipient && formData.economicStatus;
      case 4:
        return formData.workExperience.isNew !== undefined;
      case 5:
        return formData.desiredPrograms.first;
      default:
        return true;
    }
  };

  const handlePostcodeSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 팝업에서 검색결과 항목을 클릭했을때 실행할 코드를 작성하는 부분입니다.
        setFormData(prev => ({
          ...prev,
          postcode: data.zonecode,
          address: data.address,
          detailAddress: '',
        }));
      }
    }).open();
  };

  const handleFamilyRelationChange = (relation, field, value) => {
    setFormData(prev => ({
      ...prev,
      familyRelations: {
        ...prev.familyRelations,
        [relation]: {
          ...prev.familyRelations[relation],
          [field]: value
        }
      }
    }));
  };

  const handleEducationSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      education: value
    }));
  };

  const handleHouseholdSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      householdType: value
    }));
  };

  const handleSubmit = () => {
    if (!validateAllFields()) {
      alert('모든 필수 항목을 입력해주세요.');
      return;
    }

    console.log('제출된 데이터:', formData);
    
    // TODO: 서버로 데이터 전송 또는 PDF 생성 로직 추가
  };

  const validateAllFields = () => {
    if (!formData.name || !formData.residentNumber1 || !formData.residentNumber2) {
      return false;
    }

    if (!formData.education || !formData.householdType) {
      return false;
    }

    if (!formData.basicLivingRecipient || !formData.economicStatus) {
      return false;
    }

    return true;
  };

  const renderStep = () => {
    switch(step) {
      case 1:
        return (
          <div className="hmk-form-section">
            <h2>기본 정보를 알려주세요</h2>
            
            {/* 사진 업로드 - 카메라 아이콘으로 직관적 표시 */}
            <div className="hmk-photo-upload-card">
              <div className="hmk-photo-preview-area">
                {photoPreview ? (
                  <img src={photoPreview} alt="미리보기" />
                ) : (
                  <div className="hmk-camera-icon">
                    <i className="fas fa-camera"></i>
                    <p>사진을 찍거나 선택해주세요</p>
                  </div>
                )}
              </div>
              <input
                type="file"
                accept="image/*"
                capture="user"
                onChange={handlePhotoCapture}
                className="hmk-hidden-input"
              />
            </div>

            {/* 이름 입력 - 큰 폰트와 간단한 입력창 */}
            <div className="hmk-input-card">
              <label>성함을 알려주세요</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="예: 홍길동"
                className="hmk-large-input"
              />
            </div>

            {/* 주민등록번호 - 분리된 입력창과 자동 포커스 이동 */}
            <div className="hmk-input-card">
              <label>주민등록번호를 입력해주세요</label>
              <div className="hmk-resident-number">
                <input
                  type="text"
                  name="residentNumber1"
                  maxLength="6"
                  value={formData.residentNumber1}
                  onChange={handleChange}
                  placeholder="앞 6자리"
                />
                <span>-</span>
                <input
                  type="password"
                  name="residentNumber2"
                  maxLength="7"
                  value={formData.residentNumber2}
                  onChange={handleChange}
                  placeholder="뒤 7자리"
                />
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="hmk-form-section">
            <h2>학력을 선택해주세요</h2>
            <div className="hmk-card-grid">
              {educationOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.education === option.value ? 'selected' : ''}`}
                  onClick={() => handleEducationSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      case 3:
        return (
          <div className="hmk-form-section">
            <h2>현재 거주 형태를 선택해주세요</h2>
            <div className="hmk-card-grid">
              {householdOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.householdType === option.value ? 'selected' : ''}`}
                  onClick={() => handleHouseholdSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      // ... 다른 step들도 비슷한 카드 형식으로 구현
    }
  };

  return (
    <div className="hmk-form-container">
      <div className="hmk-progress-bar">
        <div 
          className="hmk-progress-fill"
          style={{width: `${(step / totalSteps) * 100}%`}}
        />
      </div>
      
      {renderStep()}
      
      <div className="hmk-navigation-buttons">
        {step > 1 && (
          <button 
            className="hmk-prev-button"
            onClick={prevStep}
          >
            이전
          </button>
        )}
        
        {step < totalSteps ? (
          <button 
            className="hmk-next-button"
            onClick={nextStep}
          >
            다음
          </button>
        ) : (
          <button 
            className="hmk-submit-button"
            onClick={handleSubmit}
          >
            신청서 작성 완료
          </button>
        )}
      </div>
    </div>
  );
};

export default SeniorJobApplicationForm;
