import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone, faArrowLeft, faCheck, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import '../../assets/css/resumeSequence.css';
import { voiceService } from '../../api/voiceService';
import { useVoiceRecording } from '../../hooks/useVoiceRecording';
import axios from 'axios';

// 미리 녹음된 안내 음성 파일 import
const audioGuides = {
    1: '/static/audio/guides/name_question.mp3',    // "이름이 어떻게 되세요?"
    2: '/static/audio/guides/age_question.mp3',     // "나이가 어떻게 되세요?"
    3: '/static/audio/guides/phone_question.mp3',   // "전화번호가 어떻게 되세요?"
    4: '/static/audio/guides/address_question.mp3'  // "어디 사세요?"
};

const ResumeSequence = () => {
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        phone: '',
        address: ''
    });
    const [completedSteps, setCompletedSteps] = useState([]);

    // Section 컴포넌트
    const Section = ({ step, question, field, value, onChange, onConfirm, isCompleted }) => {
        const [tempValue, setTempValue] = useState(value);  // 임시 입력값 상태
        const [showConfirm, setShowConfirm] = useState(false);  // 확인 버튼 표시 여부

        // 입력값 변경 핸들러
        const handleChange = (e) => {
            const newValue = e.target.value;
            setTempValue(newValue);
            onChange(field, newValue);
            setShowConfirm(newValue.trim().length > 0);
        };

        // 확인 버튼 클릭 핸들러
        const handleConfirmClick = () => {
            if (tempValue.trim()) {
                onConfirm(step);
            }
        };

        return (
            <div className={`hmk-sequence-section ${isCompleted ? 'completed' : ''}`}>
                <div className="hmk-sequence-content">
                    <h2 className="hmk-sequence-question">{question}</h2>
                    <div className="hmk-input-container">
                        <input
                            type={field === 'phone' ? 'tel' : 'text'}
                            className="hmk-sequence-input"
                            value={tempValue}
                            onChange={handleChange}
                            placeholder={field === 'phone' ? '전화번호를 입력해주세요' : 
                                       field === 'address' ? '주소를 입력해주세요' : '입력해주세요'}
                        />
                    </div>
                    {showConfirm && (
                        <button 
                            className="hmk-confirm-button"
                            onClick={handleConfirmClick}
                        >
                            확인
                        </button>
                    )}
                </div>
            </div>
        );
    };

    // 입력값 변경 핸들러
    const handleInputChange = (field, value) => {
        setFormData(prev => ({
            ...prev,
            [field]: value
        }));
    };

    // 확인 버튼 핸들러
    const handleConfirm = (step) => {
        if (!completedSteps.includes(step)) {
            setCompletedSteps(prev => [...prev, step]);
            setCurrentStep(step + 1);
        }
    };

    return (
        <div className="hmk-sequence-container">
            <div className="hmk-sequence-header">
                <button 
                    className="hmk-back-button"
                    onClick={() => navigate(-1)}
                >
                    <FontAwesomeIcon icon={faArrowLeft} />
                </button>
                <div className="hmk-progress-container">
                    <div className="hmk-progress-bar">
                        <div className="hmk-progress-track">
                            <div 
                                className="hmk-progress-fill"
                                style={{ width: `${((completedSteps.length) / (4 - 1)) * 100}%` }}
                            ></div>
                        </div>
                        <div className="hmk-progress-steps">
                            {[
                                { label: "이름", step: 1 },
                                { label: "나이", step: 2 },
                                { label: "전화번호", step: 3 },
                                { label: "주소", step: 4 }
                            ].map((item) => (
                                <div 
                                    key={item.step}
                                    className={`hmk-step ${
                                        completedSteps.includes(item.step) ? 'completed' : 
                                        currentStep === item.step ? 'active' : ''
                                    }`}
                                >
                                    <div className="hmk-step-circle">
                                        {completedSteps.includes(item.step) ? 
                                            <FontAwesomeIcon icon={faCheck} /> : 
                                            item.step
                                        }
                                    </div>
                                    <span className="hmk-step-label">{item.label}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            <div className="hmk-sections-container">
                <Section 
                    step={1}
                    question="이름이 어떻게 되세요?"
                    field="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    onConfirm={handleConfirm}
                    isCompleted={completedSteps.includes(1)}
                />
                {completedSteps.includes(1) && (
                    <Section 
                        step={2}
                        question="나이가 어떻게 되세요?"
                        field="age"
                        value={formData.age}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(2)}
                    />
                )}
                {completedSteps.includes(2) && (
                    <Section 
                        step={3}
                        question="전화번호가 어떻게 되세요?"
                        field="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(3)}
                    />
                )}
                {completedSteps.includes(3) && (
                    <Section 
                        step={4}
                        question="어디 사세요?"
                        field="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(4)}
                    />
                )}
            </div>
        </div>
    );
};

export default ResumeSequence;
