import { StyleSheet } from '@react-pdf/renderer';

export const styles = StyleSheet.create({
  page: {
    padding: '10mm',
    fontFamily: 'NanumGothic',
    fontSize: 10,
  },
  header: {
    backgroundColor: '#00ffff',
    padding: '2mm',
    marginBottom: '5mm',
  },
  formNumber: {
    fontSize: 11,
  },
  title: {
    fontSize: 12,
    textAlign: 'center',
    marginBottom: '2mm',
  },
  subTitle: {
    fontSize: 10,
    marginBottom: '5mm',
  },
  table: {
    width: '100%',
    border: '1pt solid black',
  },
  row: {
    flexDirection: 'row',
    borderBottom: '1pt solid black',
  },
  photoCell: {
    width: '20%',
    borderRight: '1pt solid black',
    padding: '2mm',
    height: '40mm',
  },
  nameCell: {
    width: '40%',
    borderRight: '1pt solid black',
  },
  nameRow: {
    flexDirection: 'row',
    borderBottom: '1pt solid black',
    padding: '2mm',
  },
  phoneCell: {
    width: '40%',
    padding: '2mm',
  },
  label: {
    width: '25%',
    borderRight: '1pt solid black',
    padding: '2mm',
  },
  value: {
    flex: 1,
    padding: '2mm',
  },
  checkboxRow: {
    flexDirection: 'row',
    padding: '2mm',
    gap: '5mm',
  },
  checkbox: {
    marginRight: '1mm',
  },
  threeColumns: {
    flex: 1,
    flexDirection: 'row',
    padding: '2mm',
  },
  column: {
    flex: 1,
    borderRight: '1pt solid black',
    padding: '2mm',
  },
  lastColumn: {
    flex: 1,
    padding: '2mm',
  },
  lastRow: {
    flexDirection: 'row',
    borderBottom: 'none',
  }
});
