import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone, faArrowLeft, faCheck, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import '../../assets/css/resumeSequence.css';
import { voiceService } from '../../api/voiceService';
import { useVoiceRecording } from '../../hooks/useVoiceRecording';
import axios from 'axios';

const ResumeSequence = () => {
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState(() => {
        // 로컬 스토리지에서 기존 데이터 불러오기
        const savedData = localStorage.getItem('resumeFormData');
        return savedData ? JSON.parse(savedData) : {
            name: '',
            age: '',
            phone: '',
            address: '',
        };
    });
    const [isListening, setIsListening] = useState(false);
    const [completedSteps, setCompletedSteps] = useState([]);
    const inputRef = useRef(null);
    const sectionsRef = useRef([]);
    const { isRecording, error, startRecording } = useVoiceRecording();
    const [audioBlob, setAudioBlob] = useState(null);
    const [isProcessing, setIsProcessing] = useState(false);

    // 폼 데이터가 변경될 때마다 로컬 스토리지에 저장
    useEffect(() => {
        localStorage.setItem('resumeFormData', JSON.stringify(formData));
    }, [formData]);

    // 모든 단계가 완료되었는지 확인
    const isAllCompleted = completedSteps.length === 4;

    // 폼 제출 처리
    const handleSubmit = async () => {
        try {
            // 서버로 데이터 전송
            const response = await axios.post('http://localhost:8000/api/resume', formData);
            if (response.data.success) {
                // 성공 시 로컬 스토리지 클리어
                localStorage.removeItem('resumeFormData');
                // 다음 페이지로 이동
                navigate('/success');
            }
        } catch (error) {
            console.error('폼 제출 중 오류:', error);
        }
    };

    // TTS 기능 - 질문하기
    const speak = async (text) => {
        try {
            console.log('TTS 요청 시작:', text);
            const audioUrl = await voiceService.textToSpeech(text);
            const fullUrl = `http://localhost:8000${audioUrl}`;
            
            // 오디오 재생이 완료될 때까지 대기
            return new Promise((resolve) => {
                const audio = new Audio(fullUrl);
                audio.onended = () => resolve();  // 재생 완료 시 Promise 해결
                audio.play().catch(error => {
                    console.error('오디오 재생 중 오류:', error);
                    resolve();  // 에러 발생해도 진행
                });
            });
        } catch (error) {
            console.error('TTS 실행 중 오류 발생:', error);
        }
    };

    // STT 기능 - 답변 받기
    const startListening = async (field) => {
        if (isProcessing) return;
        
        try {
            setIsProcessing(true);
            const mediaRecorder = await startRecording();
            let chunks = [];

            mediaRecorder.ondataavailable = (e) => {
                chunks.push(e.data);
            };
            
            // 녹음 시간 조정 (전화번호는 더 길게)
            const recordingTime = field === 'phone' ? 5000 : 4000;  // 전화번호는 5초, 나머지는 4초
            
            setTimeout(() => {
                if (mediaRecorder?.state === 'recording') {
                    mediaRecorder.stop();
                }
            }, recordingTime);

            mediaRecorder.onstop = async () => {
                try {
                    const blob = new Blob(chunks, { type: 'audio/webm' });
                    let text = await voiceService.speechToText(blob);
                    
                    // 전화번호 형식 처리
                    if (field === 'phone') {
                        // 숫자만 추출
                        const numbers = text.replace(/[^0-9]/g, '');
                        
                        // 전화번호 형식으로 변환 (010-XXXX-XXXX)
                        if (numbers.length >= 10) {
                            text = numbers.replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3');
                        }
                    }

                    if (text) {
                        setFormData(prev => {
                            const newData = {
                                ...prev,
                                [field]: text
                            };
                            localStorage.setItem('resumeFormData', JSON.stringify(newData));
                            return newData;
                        });

                        // 잠시 대기 후 다음 단계로 진행
                        setTimeout(() => {
                            handleConfirm(currentStep);
                        }, 1000);  // 1초 대기
                    }
                } catch (error) {
                    console.error('음성 인식 중 오류:', error);
                } finally {
                    setIsProcessing(false);
                }
            };
        } catch (error) {
            console.error('음성 녹음 중 오류:', error);
            setIsProcessing(false);
        }
    };

    // 단계 진행 처리
    const handleConfirm = async (step) => {
        if (!completedSteps.includes(step)) {
            setCompletedSteps(prev => [...prev, step]);
            setCurrentStep(step + 1);
            
            // 다음 섹션으로 스크롤
            setTimeout(() => {
                const nextSection = sectionsRef.current[step];
                if (nextSection) {
                    nextSection.scrollIntoView({ 
                        behavior: 'smooth',
                        block: 'start'
                    });
                }
            }, 100);

            // 다음 질문 읽어주기
            const nextQuestion = getQuestionForStep(step + 1);
            if (nextQuestion) {
                await speak(nextQuestion);  // 질문이 끝날 때까지 대기
            }
        }
    };

    // 질문 텍스트 가져오기
    const getQuestionForStep = (step) => {
        switch (step) {
            case 1:
                return "이름이 어떻게 되세요?";
            case 2:
                return "나이가 어떻게 되세요?";
            case 3:
                return "전화번호가 어떻게 되세요?";
            case 4:
                return "어디 사세요?";
            default:
                return "";
        }
    };

    // 나이 선택을 위한 버튼 그룹 컴포넌트
    const AgeButtonGroup = ({ value, onChange, disabled }) => (
        <div className="hmk-age-buttons">
            {['50대', '60대', '70대', '80대', '90대 이상'].map((age) => (
                <button
                    key={age}
                    type="button"
                    className={`hmk-age-button ${value === age ? 'selected' : ''}`}
                    onClick={() => onChange('age', age)}
                    disabled={disabled}
                >
                    {age}
                </button>
            ))}
        </div>
    );

    // 각 섹션 컴포넌트
    const Section = ({ step, question, field, value, onChange, onConfirm, isCompleted }) => {
        const [isEditing, setIsEditing] = useState(false);

        const handleInputChange = (e) => {
            e.preventDefault();  // 이벤트 기본 동작 방지
            e.stopPropagation(); // 이벤트 전파 방지
            const newValue = e.target.value;
            onChange(field, newValue);
        };

        const handleUpdate = (e) => {  // handleUpdate 함수 추가
            e.stopPropagation();
            setIsEditing(false);
            onConfirm(step);
        };

        return (
            <div 
                ref={el => sectionsRef.current[step-1] = el}
                className={`hmk-sequence-section ${isCompleted ? 'completed' : ''} ${currentStep === step ? 'active' : ''}`}
            >
                <div className="hmk-sequence-content">
                    <h2 className="hmk-sequence-question">
                        {question}
                    </h2>

                    {step === 2 ? (
                        <div className="hmk-input-container">
                            <AgeButtonGroup 
                                value={value}
                                onChange={onChange}
                                disabled={isCompleted && !isEditing}
                            />
                            {(!isCompleted || isEditing) && (
                                <button 
                                    className="hmk-confirm-button"
                                    onClick={isEditing ? handleUpdate : () => onConfirm(step)}
                                    disabled={!value}
                                >
                                    <FontAwesomeIcon icon={isEditing ? faCheck : faArrowDown} />
                                </button>
                            )}
                        </div>
                    ) : (
                        <div className="hmk-input-container" onClick={(e) => e.stopPropagation()}>
                            <input
                                type={field === 'phone' ? 'tel' : 'text'}
                                className="hmk-sequence-input"
                                placeholder={
                                    field === 'phone' ? '010-0000-0000' :
                                    field === 'address' ? '시/군/구까지 입력해주세요' :
                                    '입력해주세요'
                                }
                                value={value}
                                onChange={handleInputChange}
                                onClick={(e) => e.stopPropagation()}  // 클릭 이벤트 전파 방지
                                disabled={isCompleted && !isEditing}
                            />
                            {(!isCompleted || isEditing) && (
                                <>
                                    <button 
                                        className={`hmk-voice-button ${isRecording ? 'recording' : ''}`}
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            startListening(field);
                                        }}
                                    >
                                        <FontAwesomeIcon icon={faMicrophone} />
                                    </button>
                                    <button 
                                        className="hmk-confirm-button"
                                        onClick={isEditing ? handleUpdate : () => onConfirm(step)}
                                        disabled={!value.trim()}
                                    >
                                        <FontAwesomeIcon icon={isEditing ? faCheck : faArrowDown} />
                                    </button>
                                </>
                            )}
                        </div>
                    )}
                </div>
            </div>
        );
    };

    // 컴포넌트 마운트 시 로컬 스토리지 초기화
    useEffect(() => {
        // 로컬 스토리지 초기화
        localStorage.removeItem('resumeFormData');
        
        // 첫 번째 질문 읽기
        const firstQuestion = getQuestionForStep(1);
        if (firstQuestion) {
            speak(firstQuestion);
        }
        
        // 컴포넌트 언마운트 시에도 로컬 스토리지 초기화
        return () => {
            localStorage.removeItem('resumeFormData');
        };
    }, []);  // 빈 배열을 넣어 마운트/언마운트 시에만 실행

    return (
        <div 
            className="hmk-sequence-container"
            onSubmit={e => e.preventDefault()}
        >
            <div className="hmk-sequence-header">
                <button 
                    className="hmk-back-button"
                    onClick={() => navigate(-1)}
                >
                    <FontAwesomeIcon icon={faArrowLeft} />
                </button>
                <div className="hmk-progress-bar">
                    <div className="hmk-progress-track">
                        <div 
                            className="hmk-progress-fill"
                            style={{ width: `${(completedSteps.length / 10) * 100}%` }}
                        ></div>
                    </div>
                    <span className="hmk-progress-step">{completedSteps.length}/10</span>
                </div>
            </div>

            <div className="hmk-sections-container">
                <Section 
                    step={1}
                    question="이름이 어떻게 되세요?"
                    field="name"
                    value={formData.name}
                    onChange={(field, value) => setFormData(prev => ({...prev, [field]: value}))}
                    onConfirm={handleConfirm}
                    isCompleted={completedSteps.includes(1)}
                />
                {completedSteps.includes(1) && (
                    <Section 
                        step={2}
                        question="나이가 어떻게 되세요?"
                        field="age"
                        value={formData.age}
                        onChange={(field, value) => setFormData(prev => ({...prev, [field]: value}))}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(2)}
                    />
                )}
                {completedSteps.includes(2) && (
                    <Section 
                        step={3}
                        question="전화번호가 어떻게 되세요?"
                        field="phone"
                        value={formData.phone}
                        onChange={(field, value) => setFormData(prev => ({...prev, [field]: value}))}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(3)}
                    />
                )}
                {completedSteps.includes(3) && (
                    <Section 
                        step={4}
                        question="어디 사세요?"
                        field="address"
                        value={formData.address}
                        onChange={(field, value) => setFormData(prev => ({...prev, [field]: value}))}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(4)}
                    />
                )}
            </div>

            {/* 모든 단계가 완료되면 제출 버튼 표시 */}
            {isAllCompleted && (
                <div className="hmk-sequence-footer">
                    <button 
                        className="hmk-submit-button"
                        onClick={handleSubmit}
                    >
                        제출하기
                    </button>
                </div>
            )}
        </div>
    );
};

export default ResumeSequence;
