import { PDFDocument, rgb } from 'pdf-lib';
import { useState } from 'react';

// 나눔고딕 폰트 base64 문자열 (미리 변환된 폰트 파일)
const NANUM_GOTHIC_BASE64 = 'YOUR_BASE64_FONT_STRING';

const ResumeTemplate = ({ formData }) => {
  const [loading, setLoading] = useState(false);

  const fillPdfForm = async () => {
    try {
      setLoading(true);
      
      // PDF 템플릿 파일 로드
      const formUrl = '/seniorjobhelper.pdf';
      const response = await fetch(formUrl);
      if (!response.ok) {
        throw new Error('PDF 템플릿을 찾을 수 없습니다.');
      }
      
      const existingPdfBytes = await response.arrayBuffer();
      
      // PDF 문서 로드
      const pdfDoc = await PDFDocument.load(existingPdfBytes, { 
        updateMetadata: false,
        ignoreEncryption: true
      });

      // UTF-8 메타데이터 설정
      pdfDoc.setTitle('노인일자리신청서', { encoding: 'utf-8' });
      
      const page = pdfDoc.getPages()[0];
      
      // 폰트 데이터를 base64에서 디코딩
      const fontBytes = Uint8Array.from(atob(NANUM_GOTHIC_BASE64), c => c.charCodeAt(0));
      
      // 한글 폰트 임베드
      const customFont = await pdfDoc.embedFont(fontBytes);
      
      // 기본 텍스트 스타일
      const textOptions = {
        font: customFont,
        size: 12,
        color: rgb(0, 0, 0)
      };

      // 기본 정보 입력
      page.drawText(formData.name, { x: 162, y: 842 - 215, ...textOptions });
      page.drawText(`${formData.residentNumber1}-${formData.residentNumber2}`, 
        { x: 150, y: 842 - 261, ...textOptions });
      page.drawText(formData.address, { x: 164, y: 842 - 290, ...textOptions });
      page.drawText(formData.mobilePhone, { x: 319, y: 842 - 215, ...textOptions });

      // 체크박스 표시 (예: 학력)
      if (formData.education) {
        const educationX = {
          '무학': 67,
          '초졸': 120,
          '중졸': 180,
          '고졸': 240,
          '전문대졸': 300,
          '대졸이상': 360
        };
        
        if (educationX[formData.education]) {
          page.drawText('✓', { 
            x: educationX[formData.education], 
            y: 842 - 320, 
            size: 14,
            ...textOptions 
          });
        }
      }

      // 나머지 필드들도 원본 PDF의 좌표에 맞춰 입력
      // ... (원본 PDF의 각 필드 위치에 맞게 추가)

      // PDF 저장 및 다운로드
      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      
      // 새 탭에서 PDF 열기
      window.open(url, '_blank');
      
      // 다운로드
      const link = document.createElement('a');
      link.href = url;
      link.download = `${formData.name}_노인일자리신청서.pdf`;
      link.click();
      
      // 메모리 정리
      URL.revokeObjectURL(url);
      
    } catch (error) {
      console.error('PDF 생성 중 오류 발생:', error);
      alert('PDF 생성 중 오류가 발생했습니다: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pdf-container">
      <button 
        onClick={fillPdfForm} 
        className="hmk-button hmk-preview-button"
        disabled={loading}
      >
        {loading ? '처리 중...' : '신청서 미리보기 및 다운로드'}
      </button>
    </div>
  );
};

export default ResumeTemplate;
