import { PDFDocument, rgb, StandardFonts } from 'pdf-lib';
import { useState } from 'react';
// PDF 템플릿 import
import pdfTemplate from '../assets/seniorjobhelper.pdf';

const ResumeTemplate = ({ formData }) => {
  const [pdfUrl, setPdfUrl] = useState(null);

  const fillPdfForm = async () => {
    try {
      // PDF 템플릿 파일을 직접 import해서 사용
      const existingPdfBytes = await fetch(pdfTemplate).then(res => res.arrayBuffer());
      
      // PDF 문서 로드
      const pdfDoc = await PDFDocument.load(existingPdfBytes);
      const page = pdfDoc.getPages()[0];
      
      // 폰트 설정
      const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica);
      
      // 기본 텍스트 스타일
      const textOptions = {
        font: helveticaFont,
        size: 10,
        color: rgb(0, 0, 0)
      };

      // 기본 정보
      page.drawText(formData.name, { x: 162, y: 842 - 215, ...textOptions });
      page.drawText(`${formData.residentNumber1}-${formData.residentNumber2}`, 
        { x: 150, y: 842 - 261, ...textOptions });
      page.drawText(formData.address, { x: 164, y: 842 - 290, ...textOptions });
      page.drawText(formData.mobilePhone, { x: 319, y: 842 - 215, ...textOptions });

      // 최종학력
      if (formData.education) {
        const educationX = {
          '무학': 67,
          '초졸': 120,
          '중졸': 180,
          '고졸': 240,
          '전문대졸': 300,
          '대졸이상': 360
        };
        page.drawText('■', { x: educationX[formData.education], y: 842 - 321, ...textOptions });
      }

      // 세대구성 형태
      if (formData.householdType) {
        const householdX = {
          '독신': 62,
          '가족동거(노부모,손자녀,장애인)': 140,
          '노인부부': 220,
          '가족동거(경제능력가족)': 300,
          '기타': 380
        };
        page.drawText('■', { x: householdX[formData.householdType], y: 842 - 354, ...textOptions });
      }

      // 동거가족 정보
      const familyCount = (formData.spouse ? 1 : 0) + 
                         (formData.childrenCount || 0) + 
                         (formData.grandchildrenCount || 0);
      if (familyCount > 0) {
        page.drawText(`${familyCount}명`, { x: 67, y: 842 - 387, ...textOptions });
        page.drawText(`(배우자: ${formData.spouse ? '1' : '0'}, 자녀: ${formData.childrenCount || '0'}, 손: ${formData.grandchildrenCount || '0'})`, 
          { x: 120, y: 842 - 387, ...textOptions });
      }

      // 일자리사업 참여경력
      if (!formData.workExperience.isNew) {
        page.drawText('■', { x: 50, y: 842 - 419, ...textOptions });
      }

      // 건강상태
      const healthY = 460;
      const healthPositions = {
        '매우건강': 100,
        '건강': 180,
        '보통': 260,
        '나쁨': 340,
        '매우나쁨': 420
      };
      if (formData.healthStatus) {
        page.drawText('■', { x: healthPositions[formData.healthStatus], y: healthY, ...textOptions });
      }

      // 희망 직종
      if (formData.desiredPrograms.first) {
        page.drawText(formData.desiredPrograms.first, { x: 120, y: 420, ...textOptions });
      }
      if (formData.desiredPrograms.second) {
        page.drawText(formData.desiredPrograms.second, { x: 120, y: 400, ...textOptions });
      }
      if (formData.desiredPrograms.third) {
        page.drawText(formData.desiredPrograms.third, { x: 120, y: 380, ...textOptions });
      }

      // 건강보험 직장가입자 여부
      const insuranceY = 340;
      if (formData.volunteerWork.healthInsurance === 'true') {
        page.drawText('■', { x: 100, y: insuranceY, ...textOptions }); // 해당
      } else {
        page.drawText('■', { x: 200, y: insuranceY, ...textOptions }); // 비해당
      }

      // 신청 동기
      if (formData.applicationMotives.length > 0) {
        const motivesY = 300;
        formData.applicationMotives.forEach((motive, index) => {
          page.drawText('■', { x: 100 + (index * 80), y: motivesY, ...textOptions });
        });
      }

      // PDF 저장
      const modifiedPdfBytes = await pdfDoc.save();
      
      // Blob 생성 및 URL 생성
      const blob = new Blob([modifiedPdfBytes], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      
      // 새 탭에서 PDF 열기
      window.open(url, '_blank');
      
      // URL 상태 저장 (다운로드용)
      setPdfUrl(url);

    } catch (error) {
      console.error('PDF 생성 중 오류 발생:', error);
      alert('PDF 생성 중 오류가 발생했습니다: ' + error.message);
    }
  };

  const handleDownload = () => {
    if (pdfUrl) {
      const link = document.createElement('a');
      link.href = pdfUrl;
      link.download = `${formData.name}_노인일자리신청서.pdf`;
      link.click();
      
      // URL 정리
      URL.revokeObjectURL(pdfUrl);
      setPdfUrl(null);
    }
  };

  return (
    <div className="pdf-container">
      <button onClick={fillPdfForm} className="hmk-button hmk-preview-button">
        신청서 미리보기
      </button>
      
      {pdfUrl && (
        <div className="pdf-viewer">
          <button onClick={handleDownload} className="hmk-button hmk-download-button">
            PDF 다운로드
          </button>
        </div>
      )}
    </div>
  );
};

export default ResumeTemplate;
