import React, { useState } from 'react';

const APIKeyManagement = ({ selectedAPI }) => {
    const [apiKey, setApiKey] = useState('');
    const [isEditing, setIsEditing] = useState(false);

    const handleSave = () => {
        // API 키 저장 로직
        setIsEditing(false);
    };

    const handleDelete = () => {
        // API 키 삭제 로직
        setApiKey('');
    };

    return (
        <div className="hmk-manage-api-key">
            <h3>API 키 관리</h3>
            <div className="hmk-manage-api-key-content">
                {isEditing ? (
                    <div className="hmk-manage-api-key-edit">
                        <input
                            type="password"
                            value={apiKey}
                            onChange={(e) => setApiKey(e.target.value)}
                            placeholder={`${selectedAPI} API 키 입력`}
                        />
                        <button 
                            className="hmk-manage-button"
                            onClick={handleSave}
                        >
                            저장
                        </button>
                        <button 
                            className="hmk-manage-button"
                            onClick={() => setIsEditing(false)}
                        >
                            취소
                        </button>
                    </div>
                ) : (
                    <div className="hmk-manage-api-key-display">
                        <span>{apiKey ? '••••••••' : 'API 키가 설정되지 않았습니다'}</span>
                        <button 
                            className="hmk-manage-button"
                            onClick={() => setIsEditing(true)}
                        >
                            {apiKey ? '수정' : '추가'}
                        </button>
                        {apiKey && (
                            <button 
                                className="hmk-manage-button danger"
                                onClick={handleDelete}
                            >
                                삭제
                            </button>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default APIKeyManagement;
