import { useState } from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const ResumeTemplate = ({ formData }) => {
  const [loading, setLoading] = useState(false);

  const fillPdfForm = async () => {
    try {
      setLoading(true);
      
      // PDF 생성
      const doc = new jsPDF();
      
      // 한글 폰트 추가
      doc.addFont('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2107@1.1/Pretendard-Regular.woff', 
                  'Pretendard', 
                  'normal');
      doc.setFont('Pretendard');
      
      // 제목
      doc.setFontSize(22);
      doc.text('노인일자리 신청서', 105, 20, { align: 'center' });
      
      // 기본 정보
      doc.setFontSize(12);
      doc.text(`성명: ${formData.name}`, 20, 40);
      doc.text(`주민등록번호: ${formData.residentNumber1}-${formData.residentNumber2}`, 20, 50);
      doc.text(`주소: ${formData.address} ${formData.detailAddress}`, 20, 60);
      doc.text(`연락처: ${formData.mobilePhone}`, 20, 70);
      
      // 학력
      doc.text(`최종학력: ${formData.education}`, 20, 90);
      
      // 세대구성
      doc.text(`세대구성: ${formData.householdType}`, 20, 100);
      
      // 동거가족
      const familyCount = (formData.spouse ? 1 : 0) + 
                         (formData.childrenCount || 0) + 
                         (formData.grandchildrenCount || 0);
      doc.text(`동거가족: ${familyCount}명`, 20, 110);
      
      // 희망 직종
      doc.text('희망 직종', 20, 130);
      doc.text(`1순위: ${formData.desiredPrograms.first}`, 30, 140);
      if (formData.desiredPrograms.second) {
        doc.text(`2순위: ${formData.desiredPrograms.second}`, 30, 150);
      }
      if (formData.desiredPrograms.third) {
        doc.text(`3순위: ${formData.desiredPrograms.third}`, 30, 160);
      }
      
      // 신청 동기
      doc.text('신청 동기:', 20, 180);
      formData.applicationMotives.forEach((motive, index) => {
        doc.text(`- ${motive}`, 30, 190 + (index * 10));
      });
      
      // PDF 저장 및 다운로드
      const pdfOutput = doc.output('blob');
      const url = URL.createObjectURL(pdfOutput);
      
      // 새 탭에서 PDF 열기
      window.open(url, '_blank');
      
      // 다운로드
      const link = document.createElement('a');
      link.href = url;
      link.download = `${formData.name}_노인일자리신청서.pdf`;
      link.click();
      
      // 메모리 정리
      URL.revokeObjectURL(url);
      
    } catch (error) {
      console.error('PDF 생성 중 오류 발생:', error);
      alert('PDF 생성 중 오류가 발생했습니다: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pdf-container">
      <button 
        onClick={fillPdfForm} 
        className="hmk-button hmk-preview-button"
        disabled={loading}
      >
        {loading ? '처리 중...' : '신청서 미리보기 및 다운로드'}
      </button>
    </div>
  );
};

export default ResumeTemplate;
