import React, { useState } from 'react';
import { formatPhoneNumber } from '../../utils/formatHelper';
import { scrollToNextSection, scrollToNextButton } from '../../utils/scrollHelper';

const BasicInfoForm = ({ formData, handleChange, handlePhotoCapture, photoPreview, handlePostcodeSearch }) => {
  const [showPhotoModal, setShowPhotoModal] = useState(false);

  const handleInputChange = (e) => {
    handleChange(e);
    
    // Î™®Îì† ÌïÑÏàò ÏûÖÎ†•Ïù¥ ÏôÑÎ£åÎêòÏóàÎäîÏßÄ ÌôïÏù∏
    setTimeout(() => {
      const updatedFormData = {
        ...formData,
        [e.target.name]: e.target.value
      };
      
      if (updatedFormData.name && 
          updatedFormData.residentNumber1 && 
          updatedFormData.residentNumber2 && 
          updatedFormData.mobilePhone) {
        scrollToNextButton();
      } else {
        const currentInput = e.target;
        if (currentInput.value) {
          // ÌòÑÏû¨ ÏûÖÎ†• ÌïÑÎìúÏùò Î∂ÄÎ™® Í∑∏Î£πÏùÑ Ï∞æÍ≥†, Îã§Ïùå ÏûÖÎ†• Í∑∏Î£πÏúºÎ°ú Ïù¥Îèô
          const currentGroup = currentInput.closest('.hmk-input-group');
          if (currentGroup && currentGroup.nextElementSibling) {
            const offset = window.innerHeight * 0.2;
            const elementPosition = currentGroup.nextElementSibling.getBoundingClientRect().top + window.pageYOffset;
            
            window.scrollTo({
              top: elementPosition - offset,
              behavior: 'smooth'
            });
          }
        }
      }
    }, 100);
  };

  const handlePhoneChange = (e) => {
    const formattedNumber = formatPhoneNumber(e.target.value);
    handleInputChange({
      target: {
        name: 'mobilePhone',
        value: formattedNumber
      }
    });
  };

  const handleCameraCapture = async () => {
    try {
      // Ïπ¥Î©îÎùº Ïä§Ìä∏Î¶º Í∞ÄÏ†∏Ïò§Í∏∞
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'user',  // Ï†ÑÎ©¥ Ïπ¥Î©îÎùº ÏÇ¨Ïö©
          width: { ideal: 1280 },
          height: { ideal: 720 }
        } 
      });

      // ÎπÑÎîîÏò§ ÏöîÏÜå ÏÉùÏÑ± Î∞è Ïä§Ìä∏Î¶º Ïó∞Í≤∞
      const video = document.createElement('video');
      video.srcObject = stream;
      video.autoplay = true;

      // Î™®Îã¨ ÏÉùÏÑ±
      const modal = document.createElement('div');
      modal.className = 'hmk-camera-modal';
      modal.innerHTML = `
        <div class="hmk-camera-container">
          <video autoplay playsinline></video>
          <div class="hmk-camera-controls">
            <button class="hmk-capture-btn">ÏÇ¨ÏßÑ Ï¥¨ÏòÅ</button>
            <button class="hmk-cancel-btn">Ï∑®ÏÜå</button>
          </div>
        </div>
      `;

      document.body.appendChild(modal);
      const videoElement = modal.querySelector('video');
      videoElement.srcObject = stream;

      // ÏÇ¨ÏßÑ Ï¥¨ÏòÅ Î≤ÑÌäº Ïù¥Î≤§Ìä∏
      const captureBtn = modal.querySelector('.hmk-capture-btn');
      captureBtn.onclick = () => {
        const canvas = document.createElement('canvas');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        canvas.getContext('2d').drawImage(video, 0, 0);
        
        // Ïù¥ÎØ∏ÏßÄÎ•º File Í∞ùÏ≤¥Î°ú Î≥ÄÌôò
        canvas.toBlob((blob) => {
          const file = new File([blob], "camera_photo.jpg", { type: "image/jpeg" });
          handlePhotoCapture({ target: { files: [file] } });
          
          // Ïä§Ìä∏Î¶º Ï†ïÏßÄ Î∞è Î™®Îã¨ Ï†úÍ±∞
          stream.getTracks().forEach(track => track.stop());
          document.body.removeChild(modal);
        }, 'image/jpeg');
      };

      // Ï∑®ÏÜå Î≤ÑÌäº Ïù¥Î≤§Ìä∏
      const cancelBtn = modal.querySelector('.hmk-cancel-btn');
      cancelBtn.onclick = () => {
        stream.getTracks().forEach(track => track.stop());
        document.body.removeChild(modal);
      };

    } catch (err) {
      console.error('Ïπ¥Î©îÎùº Ï†ëÍ∑º ÏóêÎü¨:', err);
      alert('Ïπ¥Î©îÎùºÏóê Ï†ëÍ∑ºÌï† Ïàò ÏóÜÏäµÎãàÎã§. ÌååÏùº ÏóÖÎ°úÎìúÎ•º Ïù¥Ïö©Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  return (
    <div className="hmk-form-section">
      <h2 className="hmk-section-title">Í∏∞Î≥∏ Ï†ïÎ≥¥Î•º ÏïåÎ†§Ï£ºÏÑ∏Ïöî</h2>
      
      <div 
        className="hmk-photo-upload-card"
        onClick={() => setShowPhotoModal(true)}
      >
        {photoPreview ? (
          <img src={photoPreview} alt="ÎØ∏Î¶¨Î≥¥Í∏∞" className="hmk-photo-preview" />
        ) : (
          <div className="hmk-camera-icon">
            <span className="hmk-icon">üì∏</span>
            <p>Ïó¨Í∏∞Î•º ÎàåÎü¨ÏÑú ÏÇ¨ÏßÑÏùÑ Îì±Î°ùÌï¥Ï£ºÏÑ∏Ïöî</p>
          </div>
        )}
      </div>

      {showPhotoModal && (
        <div className="hmk-photo-modal">
          <div className="hmk-photo-modal-content">
            <h3>ÏÇ¨ÏßÑ Îì±Î°ù Î∞©Î≤ïÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</h3>
            <div className="hmk-photo-modal-buttons">
              <button 
                className="hmk-photo-modal-btn"
                onClick={() => {
                  handleCameraCapture();
                  setShowPhotoModal(false);
                }}
              >
                <span className="hmk-modal-icon">üì∏</span>
                Ïπ¥Î©îÎùºÎ°ú Ï¥¨ÏòÅÌïòÍ∏∞
              </button>
              <label className="hmk-photo-modal-btn">
                <span className="hmk-modal-icon">üñºÔ∏è</span>
                Í∞§Îü¨Î¶¨ÏóêÏÑú ÏÑ†ÌÉùÌïòÍ∏∞
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => {
                    handlePhotoCapture(e);
                    setShowPhotoModal(false);
                  }}
                  className="hmk-hidden-input"
                />
              </label>
            </div>
            <button 
              className="hmk-photo-modal-close"
              onClick={() => setShowPhotoModal(false)}
            >
              Ï∑®ÏÜå
            </button>
          </div>
        </div>
      )}

      <div className="hmk-input-group">
        <label className="hmk-label">ÏÑ±Ìï®ÏùÑ ÏïåÎ†§Ï£ºÏÑ∏Ïöî</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleInputChange}
          placeholder="Ïòà: ÌôçÍ∏∏Îèô"
          className="hmk-large-input"
        />
