import React, { useState } from 'react';
import '../asset/css/seniorForm.css';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ResumeTemplate from './ResumeTemplate';

const SeniorJobApplicationForm = () => {
  const [formData, setFormData] = useState({
    // ê¸°ë³¸ ì •ë³´
    name: '',
    title: '',
    residentNumber1: '',
    residentNumber2: '',
    address: '',
    homePhone: '',
    mobilePhone: '',
    familyPhone: '',
    postcode: '',
    detailAddress: '',
    photo: null,
    
    // ìµœì¢…í•™ë ¥
    education: '',
    
    // ì„¸ëŒ€êµ¬ì„± í˜•íƒœ
    householdType: '',
    
    // ë™ê±°ê°€ì¡±
    familyMembers: '',
    spouse: '',
    children: '',
    grandchildren: '',
    
    // ì¼ìžë¦¬ì‚¬ì—… ì°¸ì—¬ê²½ë ¥
    workExperience: {
      isNew: false,
      years: []
    },
    
    // ê¸°ì´ˆìƒí™œìˆ˜ê¸‰ê¶Œìž
    basicLivingRecipient: '',
    
    // ê²½ì œìƒíƒœ
    economicStatus: '',
    
    // ì£¼ê±°í˜•íƒœ
    housingType: '',
    
    // ê±´ê°•ìƒíƒœ
    healthStatus: '',
    
    // ì°¸ì—¬í¬ë§ì‚¬ì—…
    desiredPrograms: {
      first: '',
      second: '',
      third: ''
    },
    
    // ì‹ ì²­ë™ê¸°
    applicationMotives: [],
    
    // ìžì›ë´‰ì‚¬ ì°¸ì—¬
    volunteerWork: {
      participated: false,
      healthInsurance: ''
    },
    
    // ê¸°ì´ˆë…¸ë ¹ì—°ê¸ˆ
    basicPensionStatus: {
      receiving: false,
      amount: ''
    },
    
    // ìž¥ê¸°ìš”ì–‘
    longTermCare: {
      status: false
    }
  });

  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [photoPreview, setPhotoPreview] = useState(null);

  const educationOptions = [
    { value: 'ë¬´í•™', label: 'ë¬´í•™', icon: 'ðŸ“š' },
    { value: 'ì´ˆì¡¸', label: 'ì´ˆë“±í•™êµ ì¡¸ì—…', icon: 'ðŸŽ“' },
    { value: 'ì¤‘ì¡¸', label: 'ì¤‘í•™êµ ì¡¸ì—…', icon: 'ðŸŽ“' },
    { value: 'ê³ ì¡¸', label: 'ê³ ë“±í•™êµ ì¡¸ì—…', icon: 'ðŸŽ“' },
    { value: 'ì „ë¬¸ëŒ€ì¡¸', label: 'ì „ë¬¸ëŒ€í•™ ì¡¸ì—…', icon: 'ðŸŽ“' },
    { value: 'ëŒ€ì¡¸ì´ìƒ', label: 'ëŒ€í•™êµ ì¡¸ì—… ì´ìƒ', icon: 'ðŸŽ“' }
  ];

  const householdOptions = [
