import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone, faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import '../../assets/css/resumeSequence.css';

const ResumeSequence = () => {
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(1);
    const [userName, setUserName] = useState('');
    const [isListening, setIsListening] = useState(false);
    const inputRef = useRef(null);

    // TTS 기능
    const speak = (text) => {
        const utterance = new SpeechSynthesisUtterance(text);
        utterance.lang = 'ko-KR';
        window.speechSynthesis.speak(utterance);
    };

    // STT 기능
    const startListening = () => {
        if ('webkitSpeechRecognition' in window) {
            const recognition = new window.webkitSpeechRecognition();
            recognition.lang = 'ko-KR';
            recognition.interimResults = false;
            recognition.maxAlternatives = 1;

            recognition.onstart = () => {
                setIsListening(true);
            };

            recognition.onresult = (event) => {
                const transcript = event.results[0][0].transcript;
                setUserName(transcript);
                inputRef.current.value = transcript;
            };

            recognition.onend = () => {
                setIsListening(false);
            };

            recognition.start();
        }
    };

    // 컴포넌트 마운트 시 TTS 실행
    useEffect(() => {
        const timer = setTimeout(() => {
            speak('이름이 어떻게 되세요?');
        }, 500);

        return () => {
            clearTimeout(timer);
            window.speechSynthesis.cancel();
        };
    }, []);

    return (
        <div className="hmk-sequence-section">
            {/* 진행 상태 바 */}
            <div className="hmk-progress-bar">
                <div className="hmk-progress-track">
                    <div 
                        className="hmk-progress-fill"
                        style={{ width: `${(currentStep / 10) * 100}%` }}
                    ></div>
                </div>
                <span className="hmk-progress-step">{currentStep}/10</span>
            </div>

            {/* 헤더 */}
            <div className="hmk-sequence-header">
                <button 
                    className="hmk-back-button"
                    onClick={() => navigate(-1)}
                >
                    <FontAwesomeIcon icon={faArrowLeft} />
                </button>
            </div>

            {/* 메인 컨텐츠 */}
            <div className="hmk-sequence-content">
                <h2 className="hmk-sequence-question">
                    이름이 어떻게 되세요?
                </h2>

                <div className="hmk-input-container">
                    <input
                        ref={inputRef}
                        type="text"
                        className="hmk-sequence-input"
                        placeholder="이름을 입력해주세요"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                    />
                    <button 
                        className={`hmk-voice-button ${isListening ? 'listening' : ''}`}
                        onClick={startListening}
                    >
                        <FontAwesomeIcon icon={faMicrophone} />
                    </button>
                </div>
            </div>

            {/* 하단 버튼 */}
            <div className="hmk-sequence-footer">
                <button 
                    className="hmk-next-button"
                    disabled={!userName.trim()}
                    onClick={() => {
                        // 다음 단계로 이동하는 로직
                        setCurrentStep(prev => prev + 1);
                    }}
                >
                    다음
                </button>
            </div>
        </div>
    );
};

export default ResumeSequence;
