import React, { useState } from 'react';
import '../asset/css/seniorForm.css';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ResumeTemplate from './ResumeTemplate';

const SeniorJobApplicationForm = () => {
  const [formData, setFormData] = useState({
    // Í∏∞Î≥∏ Ï†ïÎ≥¥
    name: '',
    title: '',
    residentNumber1: '',
    residentNumber2: '',
    address: '',
    homePhone: '',
    mobilePhone: '',
    familyPhone: '',
    postcode: '',
    detailAddress: '',
    photo: null,
    
    // ÏµúÏ¢ÖÌïôÎ†•
    education: '',
    
    // ÏÑ∏ÎåÄÍµ¨ÏÑ± ÌòïÌÉú
    householdType: '',
    
    // ÎèôÍ±∞Í∞ÄÏ°±
    familyMembers: '',
    spouse: '',
    children: '',
    grandchildren: '',
    
    // ÏùºÏûêÎ¶¨ÏÇ¨ÏóÖ Ï∞∏Ïó¨Í≤ΩÎ†•
    workExperience: {
      isNew: false,
      years: []
    },
    
    // Í∏∞Ï¥àÏÉùÌôúÏàòÍ∏âÍ∂åÏûê
    basicLivingRecipient: '',
    
    // Í≤ΩÏ†úÏÉÅÌÉú
    economicStatus: '',
    
    // Ï£ºÍ±∞ÌòïÌÉú
    housingType: '',
    
    // Í±¥Í∞ïÏÉÅÌÉú
    healthStatus: '',
    
    // Ï∞∏Ïó¨Ìù¨ÎßùÏÇ¨ÏóÖ
    desiredPrograms: {
      first: '',
      second: '',
      third: ''
    },
    
    // Ïã†Ï≤≠ÎèôÍ∏∞
    applicationMotives: [],
    
    // ÏûêÏõêÎ¥âÏÇ¨ Ï∞∏Ïó¨
    volunteerWork: {
      participated: false,
      healthInsurance: ''
    },
    
    // Í∏∞Ï¥àÎÖ∏Î†πÏó∞Í∏à
    basicPensionStatus: {
      receiving: false,
      amount: ''
    },
    
    // Ïû•Í∏∞ÏöîÏñë
    longTermCare: {
      status: false
    }
  });

  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [photoPreview, setPhotoPreview] = useState(null);

  const educationOptions = [
    { value: 'Î¨¥Ìïô', label: 'Î¨¥Ìïô', icon: 'üìö' },
    { value: 'Ï¥àÏ°∏', label: 'Ï¥àÎì±ÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Ï§ëÏ°∏', label: 'Ï§ëÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Í≥†Ï°∏', label: 'Í≥†Îì±ÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Ï†ÑÎ¨∏ÎåÄÏ°∏', label: 'Ï†ÑÎ¨∏ÎåÄÌïô Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'ÎåÄÏ°∏Ïù¥ÏÉÅ', label: 'ÎåÄÌïôÍµê Ï°∏ÏóÖ Ïù¥ÏÉÅ', icon: 'üéì' }
  ];

  const householdOptions = [
    { value: 'ÎèÖÏã†', label: 'ÌòºÏûê ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üè†' },
    { value: 'ÎÖ∏Ïù∏Î∂ÄÎ∂Ä', label: 'Î∞∞Ïö∞ÏûêÏôÄ Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üë´' },
    { value: 'Í∞ÄÏ°±ÎèôÍ±∞(ÎÖ∏Î∂ÄÎ™®,ÏÜêÏûêÎÖÄ,Ïû•Ïï†Ïù∏)', label: 'Î∂ÄÎ™®Îãò/ÏÜêÏ£º/Ïû•Ïï†Ïù∏ Í∞ÄÏ°±Í≥º Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶' },
    { value: 'Í∞ÄÏ°±ÎèôÍ±∞(Í≤ΩÏ†úÎä•Î†•Í∞ÄÏ°±)', label: 'Í≤ΩÏ†úÌôúÎèôÏùÑ ÌïòÎäî Í∞ÄÏ°±Í≥º Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üíº' },
    { value: 'Í∏∞ÌÉÄ', label: 'Í∏∞ÌÉÄ', icon: 'üìù' }
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handlePhotoCapture = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, photo: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const nextStep = () => {
    if (validateCurrentStep()) {
      setStep(prev => Math.min(prev + 1, totalSteps));
    } else {
      alert('ÌïÑÏàò Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const prevStep = () => {
    setStep(prev => Math.max(prev - 1, 1));
  };

  const validateCurrentStep = () => {
    switch(step) {
      case 1:
        return formData.name && 
               formData.residentNumber1 && 
               formData.residentNumber2 && 
               formData.address && 
               formData.mobilePhone;
      case 2:
        // ÌïôÎ†•Í≥º ÏÑ∏ÎåÄÍµ¨ÏÑ± ÌòïÌÉúÎßå ÌïÑÏàòÎ°ú Ï≤¥ÌÅ¨
        return formData.education && formData.householdType;
      case 3:
        return formData.basicLivingRecipient && formData.economicStatus;
      case 4:
        return formData.workExperience.isNew !== undefined;
      case 5:
        // Ìù¨ÎßùÏÇ¨ÏóÖ 1ÏàúÏúÑÎäî ÌïÑÏàò
        return formData.desiredPrograms.first;
      default:
        return true;
    }
  };

  const handlePostcodeSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // ÌåùÏóÖÏóêÏÑú Í≤ÄÏÉâÍ≤∞Í≥º Ìï≠Î™©ÏùÑ ÌÅ¥Î¶≠ÌñàÏùÑÎïå Ïã§ÌñâÌï† ÏΩîÎìúÎ•º ÏûëÏÑ±ÌïòÎäî Î∂ÄÎ∂ÑÏûÖÎãàÎã§.
        setFormData(prev => ({
          ...prev,
          postcode: data.zonecode,
          address: data.address,
          detailAddress: '',
        }));
      }
    }).open();
  };

  const handleFamilyRelationChange = (relation, field, value) => {
    setFormData(prev => ({
      ...prev,
      familyRelations: {
        ...prev.familyRelations,
        [relation]: {
          ...prev.familyRelations[relation],
          [field]: value
        }
      }
    }));
  };

  const renderStep = () => {
    switch(step) {
      case 1:
        return (
          <div className="hmk-form-section">
            <h2>Í∏∞Î≥∏ Ï†ïÎ≥¥Î•º ÏïåÎ†§Ï£ºÏÑ∏Ïöî</h2>
            
            {/* ÏÇ¨ÏßÑ ÏóÖÎ°úÎìú - Ïπ¥Î©îÎùº ÏïÑÏù¥ÏΩòÏúºÎ°ú ÏßÅÍ¥ÄÏ†Å ÌëúÏãú */}
            <div className="hmk-photo-upload-card">
              <div className="hmk-photo-preview-area">
                {photoPreview ? (
                  <img src={photoPreview} alt="ÎØ∏Î¶¨Î≥¥Í∏∞" />
                ) : (
                  <div className="hmk-camera-icon">
                    <i className="fas fa-camera"></i>
                    <p>ÏÇ¨ÏßÑÏùÑ Ï∞çÍ±∞ÎÇò ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</p>
                  </div>
                )}
              </div>
              <input
                type="file"
                accept="image/*"
                capture="user"
                onChange={handlePhotoCapture}
                className="hmk-hidden-input"
              />
            </div>

            {/* Ïù¥Î¶Ñ ÏûÖÎ†• - ÌÅ∞ Ìè∞Ìä∏ÏôÄ Í∞ÑÎã®Ìïú ÏûÖÎ†•Ï∞Ω */}
            <div className="hmk-input-card">
              <label>ÏÑ±Ìï®ÏùÑ ÏïåÎ†§Ï£ºÏÑ∏Ïöî</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Ïòà: ÌôçÍ∏∏Îèô"
                className="hmk-large-input"
              />
            </div>

            {/* Ï£ºÎØºÎì±Î°ùÎ≤àÌò∏ - Î∂ÑÎ¶¨Îêú ÏûÖÎ†•Ï∞ΩÍ≥º ÏûêÎèô Ìè¨Ïª§Ïä§ Ïù¥Îèô */}
            <div className="hmk-input-card">
              <label>Ï£ºÎØºÎì±Î°ùÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</label>
              <div className="hmk-resident-number">
                <input
                  type="text"
                  name="residentNumber1"
                  maxLength="6"
                  value={formData.residentNumber1}
                  onChange={handleChange}
                  placeholder="Ïïû 6ÏûêÎ¶¨"
                />
                <span>-</span>
                <input
                  type="password"
                  name="residentNumber2"
                  maxLength="7"
                  value={formData.residentNumber2}
                  onChange={handleChange}
                  placeholder="Îí§ 7ÏûêÎ¶¨"
                />
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="hmk-form-section">
            <h2>ÌïôÎ†•ÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</h2>
            <div className="hmk-card-grid">
              {educationOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.education === option.value ? 'selected' : ''}`}
                  onClick={() => handleEducationSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      case 3:
        return (
          <div className="hmk-form-section">
            <h2>ÌòÑÏû¨ Í±∞Ï£º ÌòïÌÉúÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</h2>
            <div className="hmk-card-grid">
              {householdOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.householdType === option.value ? 'selected' : ''}`}
                  onClick={() => handleHouseholdSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      // ... Îã§Î•∏ stepÎì§ÎèÑ ÎπÑÏä∑Ìïú Ïπ¥Îìú ÌòïÏãùÏúºÎ°ú Íµ¨ÌòÑ
    }
  };

  return (
    <div className="hmk-form-container">
      <div className="hmk-progress-bar">
        <div 
          className="hmk-progress-fill"
          style={{width: `${(step / totalSteps) * 100}%`}}
        />
      </div>
      
      {renderStep()}
      
      <div className="hmk-navigation-buttons">
        {step > 1 && (
          <button 
            className="hmk-prev-button"
            onClick={prevStep}
          >
            Ïù¥Ï†Ñ
          </button>
        )}
        
        {step < totalSteps ? (
          <button 
            className="hmk-next-button"
            onClick={nextStep}
          >
            Îã§Ïùå
          </button>
        ) : (
          <button 
            className="hmk-submit-button"
            onClick={handleSubmit}
          >
            Ïã†Ï≤≠ÏÑú ÏûëÏÑ± ÏôÑÎ£å
          </button>
        )}
      </div>
    </div>
  );
};

export default SeniorJobApplicationForm;
