import { StyleSheet } from '@react-pdf/renderer';

export const styles = StyleSheet.create({
  page: {
    padding: 30,
    fontFamily: 'NanumGothic'
  },
  section: {
    margin: 10,
    padding: 10
  },
  title: {
    fontSize: 24,
    textAlign: 'center',
    marginBottom: 30
  },
  row: {
    flexDirection: 'row',
    marginBottom: 10,
    alignItems: 'center'
  },
  column: {
    flexDirection: 'column'
  },
  label: {
    width: 100,
    fontSize: 12,
    fontWeight: 'bold'
  },
  value: {
    flex: 1,
    fontSize: 12
  }
});
