import { useState } from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const ResumeTemplate = ({ formData }) => {
  const [loading, setLoading] = useState(false);

  const fillPdfForm = async () => {
    try {
      setLoading(true);
      
      // PDF 생성 (한글 지원)
      const doc = new jsPDF('p', 'mm', 'a4');
      
      // 기본 폰트로 진행 (한글은 이미지로 처리)
      doc.setFont('helvetica');
      
      // 테이블 형식으로 데이터 표시
      doc.autoTable({
        head: [['노인일자리 신청서']],
        body: [
          ['성명', formData.name],
          ['주민등록번호', `${formData.residentNumber1}-${formData.residentNumber2}`],
          ['주소', `${formData.address} ${formData.detailAddress}`],
          ['연락처', formData.mobilePhone],
          ['최종학력', formData.education],
          ['세대구성', formData.householdType],
          ['동거가족', `${(formData.spouse ? 1 : 0) + (formData.childrenCount || 0) + (formData.grandchildrenCount || 0)}명`],
          ['희망직종 1순위', formData.desiredPrograms.first],
          ['희망직종 2순위', formData.desiredPrograms.second || '-'],
          ['희망직종 3순위', formData.desiredPrograms.third || '-'],
          ['신청동기', formData.applicationMotives.join(', ')],
          ['일자리사업 참여경력', formData.workExperience.isNew ? '참여한 적 있음' : '금년도 신규참여'],
          ['건강상태', formData.healthStatus]
        ],
        theme: 'grid',
        styles: {
          fontSize: 12,
          cellPadding: 5,
          overflow: 'linebreak',
          cellWidth: 'wrap'
        },
        columnStyles: {
          0: { cellWidth: 40 },
          1: { cellWidth: 'auto' }
        },
        margin: { top: 20 }
      });
      
      // PDF 저장 및 다운로드
      const pdfOutput = doc.output('blob');
      const url = URL.createObjectURL(pdfOutput);
      
      // 새 탭에서 PDF 열기
      window.open(url, '_blank');
      
      // 다운로드
      const link = document.createElement('a');
      link.href = url;
      link.download = `${formData.name}_노인일자리신청서.pdf`;
      link.click();
      
      // 메모리 정리
      URL.revokeObjectURL(url);
      
    } catch (error) {
      console.error('PDF 생성 중 오류 발생:', error);
      alert('PDF 생성 중 오류가 발생했습니다: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pdf-container">
      <button 
        onClick={fillPdfForm} 
        className="hmk-button hmk-preview-button"
        disabled={loading}
      >
        {loading ? '처리 중...' : '신청서 미리보기 및 다운로드'}
      </button>
    </div>
  );
};

export default ResumeTemplate;
