import React, { useState } from 'react';
import '../asset/css/seniorForm.css';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ResumeTemplate from './ResumeTemplate';

const SeniorJobApplicationForm = () => {
  const [formData, setFormData] = useState({
    // Í∏∞Î≥∏ Ï†ïÎ≥¥
    name: '',
    title: '',
    residentNumber1: '',
    residentNumber2: '',
    address: '',
    homePhone: '',
    mobilePhone: '',
    familyPhone: '',
    postcode: '',
    detailAddress: '',
    photo: null,
    
    // ÏµúÏ¢ÖÌïôÎ†•
    education: '',
    
    // ÏÑ∏ÎåÄÍµ¨ÏÑ± ÌòïÌÉú
    householdType: '',
    
    // ÎèôÍ±∞Í∞ÄÏ°±
    familyMembers: '',
    spouse: '',
    children: '',
    grandchildren: '',
    
    // ÏùºÏûêÎ¶¨ÏÇ¨ÏóÖ Ï∞∏Ïó¨Í≤ΩÎ†•
    workExperience: {
      isNew: false,
      years: []
    },
    
    // Í∏∞Ï¥àÏÉùÌôúÏàòÍ∏âÍ∂åÏûê
    basicLivingRecipient: '',
    
    // Í≤ΩÏ†úÏÉÅÌÉú
    economicStatus: '',
    
    // Ï£ºÍ±∞ÌòïÌÉú
    housingType: '',
    
    // Í±¥Í∞ïÏÉÅÌÉú
    healthStatus: '',
    
    // Ï∞∏Ïó¨Ìù¨ÎßùÏÇ¨ÏóÖ
    desiredPrograms: {
      first: '',
      second: '',
      third: ''
    },
    
    // Ïã†Ï≤≠ÎèôÍ∏∞
    applicationMotives: [],
    
    // ÏûêÏõêÎ¥âÏÇ¨ Ï∞∏Ïó¨
    volunteerWork: {
      participated: false,
      healthInsurance: ''
    },
    
    // Í∏∞Ï¥àÎÖ∏Î†πÏó∞Í∏à
    basicPensionStatus: {
      receiving: false,
      amount: ''
    },
    
    // Ïû•Í∏∞ÏöîÏñë
    longTermCare: {
      status: false
    }
  });

  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [photoPreview, setPhotoPreview] = useState(null);

  const educationOptions = [
    { value: 'Î¨¥Ìïô', label: 'Î¨¥Ìïô', icon: 'üìö' },
    { value: 'Ï¥àÏ°∏', label: 'Ï¥àÎì±ÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Ï§ëÏ°∏', label: 'Ï§ëÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Í≥†Ï°∏', label: 'Í≥†Îì±ÌïôÍµê Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'Ï†ÑÎ¨∏ÎåÄÏ°∏', label: 'Ï†ÑÎ¨∏ÎåÄÌïô Ï°∏ÏóÖ', icon: 'üéì' },
    { value: 'ÎåÄÏ°∏Ïù¥ÏÉÅ', label: 'ÎåÄÌïôÍµê Ï°∏ÏóÖ Ïù¥ÏÉÅ', icon: 'üéì' }
  ];

  const householdOptions = [
    { value: 'ÎèÖÏã†', label: 'ÌòºÏûê ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üè†' },
    { value: 'ÎÖ∏Ïù∏Î∂ÄÎ∂Ä', label: 'Î∞∞Ïö∞ÏûêÏôÄ Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üë´' },
    { value: 'Í∞ÄÏ°±ÎèôÍ±∞(ÎÖ∏Î∂ÄÎ™®,ÏÜêÏûêÎÖÄ,Ïû•Ïï†Ïù∏)', label: 'Î∂ÄÎ™®Îãò/ÏÜêÏ£º/Ïû•Ïï†Ïù∏ Í∞ÄÏ°±Í≥º Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶' },
    { value: 'Í∞ÄÏ°±ÎèôÍ±∞(Í≤ΩÏ†úÎä•Î†•Í∞ÄÏ°±)', label: 'Í≤ΩÏ†úÌôúÎèôÏùÑ ÌïòÎäî Í∞ÄÏ°±Í≥º Ìï®Íªò ÏÇ¥Í≥† ÏûàÏñ¥Ïöî', icon: 'üíº' },
    { value: 'Í∏∞ÌÉÄ', label: 'Í∏∞ÌÉÄ', icon: 'üìù' }
  ];

  const economicStatusOptions = [
    { value: 'Îß§Ïö∞Ï¢ãÏùå', label: 'Îß§Ïö∞ Ï¢ãÏïÑÏöî', icon: 'üåü' },
    { value: 'Ï¢ãÏùå', label: 'Ï¢ãÏùÄ Ìé∏Ïù¥ÏóêÏöî', icon: 'üòä' },
    { value: 'Î≥¥ÌÜµ', label: 'Î≥¥ÌÜµÏù¥ÏóêÏöî', icon: 'üòê' },
    { value: 'ÎÇòÏÅ®', label: 'Ïñ¥Î†§Ïö¥ Ìé∏Ïù¥ÏóêÏöî', icon: 'üòï' },
    { value: 'Îß§Ïö∞ÎÇòÏÅ®', label: 'Îß§Ïö∞ Ïñ¥Î†§ÏõåÏöî', icon: 'üò¢' }
  ];

  const healthStatusOptions = [
    { value: 'Îß§Ïö∞Í±¥Í∞ï', label: 'Îß§Ïö∞ Í±¥Í∞ïÌï¥Ïöî', icon: 'üí™' },
    { value: 'Í±¥Í∞ï', label: 'Í±¥Í∞ïÌïú Ìé∏Ïù¥ÏóêÏöî', icon: 'üèÉ' },
    { value: 'Î≥¥ÌÜµ', label: 'Î≥¥ÌÜµÏù¥ÏóêÏöî', icon: 'üö∂' },
    { value: 'ÎÇòÏÅ®', label: 'Ïïà Ï¢ãÏùÄ Ìé∏Ïù¥ÏóêÏöî', icon: 'ü§í' },
    { value: 'Îß§Ïö∞ÎÇòÏÅ®', label: 'Îß§Ïö∞ Ïïà Ï¢ãÏïÑÏöî', icon: 'üè•' }
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handlePhotoCapture = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, photo: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const nextStep = () => {
    if (validateCurrentStep()) {
      setStep(prev => Math.min(prev + 1, totalSteps));
    } else {
      alert('ÌïÑÏàò Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const prevStep = () => {
    setStep(prev => Math.max(prev - 1, 1));
  };

  const validateCurrentStep = () => {
    switch(step) {
      case 1:
        return formData.name && 
               formData.residentNumber1 && 
               formData.residentNumber2 && 
               formData.mobilePhone;
      case 2:
        return formData.education && formData.householdType;
      case 3:
        return formData.basicLivingRecipient && formData.economicStatus;
      case 4:
        return formData.workExperience.isNew !== undefined;
      case 5:
        return formData.desiredPrograms.first;
      default:
        return true;
    }
  };

  const handlePostcodeSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // ÌåùÏóÖÏóêÏÑú Í≤ÄÏÉâÍ≤∞Í≥º Ìï≠Î™©ÏùÑ ÌÅ¥Î¶≠ÌñàÏùÑÎïå Ïã§ÌñâÌï† ÏΩîÎìúÎ•º ÏûëÏÑ±ÌïòÎäî Î∂ÄÎ∂ÑÏûÖÎãàÎã§.
        setFormData(prev => ({
          ...prev,
          postcode: data.zonecode,
          address: data.address,
          detailAddress: '',
        }));
      }
    }).open();
  };

  const handleFamilyRelationChange = (relation, field, value) => {
    setFormData(prev => ({
      ...prev,
      familyRelations: {
        ...prev.familyRelations,
        [relation]: {
          ...prev.familyRelations[relation],
          [field]: value
        }
      }
    }));
  };

  const handleEducationSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      education: value
    }));
  };

  const handleHouseholdSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      householdType: value
    }));
  };

  const handleEconomicSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      economicStatus: value
    }));
  };

  const handleHealthSelect = (value) => {
    setFormData(prev => ({
      ...prev,
      healthStatus: value
    }));
  };

  const handleSubmit = () => {
    if (!validateAllFields()) {
      alert('Î™®Îì† ÌïÑÏàò Ìï≠Î™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    console.log('Ï†úÏ∂úÎêú Îç∞Ïù¥ÌÑ∞:', formData);
    
    // TODO: ÏÑúÎ≤ÑÎ°ú Îç∞Ïù¥ÌÑ∞ Ï†ÑÏÜ° ÎòêÎäî PDF ÏÉùÏÑ± Î°úÏßÅ Ï∂îÍ∞Ä
  };

  const validateAllFields = () => {
    if (!formData.name || !formData.residentNumber1 || !formData.residentNumber2) {
      return false;
    }

    if (!formData.education || !formData.householdType) {
      return false;
    }

    if (!formData.basicLivingRecipient || !formData.economicStatus) {
      return false;
    }

    return true;
  };

  const renderStep = () => {
    switch(step) {
      case 1:
        return (
          <div className="hmk-form-section">
            <h2 className="hmk-section-title">Í∏∞Î≥∏ Ï†ïÎ≥¥Î•º ÏïåÎ†§Ï£ºÏÑ∏Ïöî</h2>
            
            <div className="hmk-photo-upload-card">
              {photoPreview ? (
                <img src={photoPreview} alt="ÎØ∏Î¶¨Î≥¥Í∏∞" className="hmk-photo-preview" />
              ) : (
                <div className="hmk-camera-icon">
                  <span className="hmk-icon">üì∏</span>
                  <p>Ïó¨Í∏∞Î•º ÎàåÎü¨ÏÑú ÏÇ¨ÏßÑÏùÑ Îì±Î°ùÌï¥Ï£ºÏÑ∏Ïöî</p>
                </div>
              )}
              <input
                type="file"
                accept="image/*"
                capture="user"
                onChange={handlePhotoCapture}
                className="hmk-hidden-input"
              />
            </div>

            <div className="hmk-input-group">
              <label className="hmk-label">ÏÑ±Ìï®ÏùÑ ÏïåÎ†§Ï£ºÏÑ∏Ïöî</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Ïòà: ÌôçÍ∏∏Îèô"
                className="hmk-large-input"
              />
            </div>

            <div className="hmk-input-group">
              <label className="hmk-label">Ï£ºÎØºÎì±Î°ùÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</label>
              <div className="hmk-resident-number">
                <input
                  type="text"
                  name="residentNumber1"
                  maxLength="6"
                  value={formData.residentNumber1}
                  onChange={handleChange}
                  placeholder="Ïïû 6ÏûêÎ¶¨"
                  className="hmk-large-input"
                />
                <span className="hmk-separator">-</span>
                <input
                  type="password"
                  name="residentNumber2"
                  maxLength="7"
                  value={formData.residentNumber2}
                  onChange={handleChange}
                  placeholder="Îí§ 7ÏûêÎ¶¨"
                  className="hmk-large-input"
                />
              </div>
            </div>

            <div className="hmk-input-group">
              <label className="hmk-label">Ìú¥ÎåÄÌè∞ Î≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</label>
              <input
                type="tel"
                name="mobilePhone"
                value={formData.mobilePhone}
                onChange={handleChange}
                placeholder="Ïòà: 010-1234-5678"
                className="hmk-large-input"
              />
            </div>

            <div className="hmk-input-group">
              <label className="hmk-label">Ï£ºÏÜåÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</label>
              <div className="hmk-address-group">
                <div className="hmk-postcode-row">
                  <input
                    type="text"
                    name="postcode"
                    value={formData.postcode}
                    className="hmk-large-input hmk-postcode"
                    placeholder="Ïö∞Ìé∏Î≤àÌò∏"
                    readOnly
                  />
                  <button 
                    type="button" 
                    className="hmk-button hmk-address-search-btn"
                    onClick={handlePostcodeSearch}
                  >
                    Ï£ºÏÜå Í≤ÄÏÉâ
                  </button>
                </div>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  className="hmk-large-input"
                  placeholder="Í∏∞Î≥∏Ï£ºÏÜå"
                  readOnly
                />
                <input
                  type="text"
                  name="detailAddress"
                  value={formData.detailAddress}
                  onChange={handleChange}
                  className="hmk-large-input"
                  placeholder="ÏÉÅÏÑ∏Ï£ºÏÜåÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
                />
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="hmk-form-section">
            <h2 className="hmk-section-title">ÌïôÎ†•ÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</h2>
            <div className="hmk-card-grid">
              {educationOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.education === option.value ? 'selected' : ''}`}
                  onClick={() => handleEducationSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      case 3:
        return (
          <div className="hmk-form-section">
            <h2 className="hmk-section-title">ÌòÑÏû¨ Í±¥Í∞ï ÏÉÅÌÉúÎäî Ïñ¥Îñ†Ïã†Í∞ÄÏöî?</h2>
            <div className="hmk-card-grid">
              {healthStatusOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.healthStatus === option.value ? 'selected' : ''}`}
                  onClick={() => handleHealthSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      case 4:
        return (
          <div className="hmk-form-section">
            <h2 className="hmk-section-title">Í≤ΩÏ†úÏ†Å ÏÉÅÌô©ÏùÄ Ïñ¥Îñ†Ïã†Í∞ÄÏöî?</h2>
            <div className="hmk-card-grid">
              {economicStatusOptions.map(option => (
                <div
                  key={option.value}
                  className={`hmk-selection-card ${formData.economicStatus === option.value ? 'selected' : ''}`}
                  onClick={() => handleEconomicSelect(option.value)}
                >
                  <span className="hmk-card-icon">{option.icon}</span>
                  <span className="hmk-card-label">{option.label}</span>
                </div>
              ))}
            </div>
          </div>
        );

      // ... Îã§Î•∏ stepÎì§ÎèÑ ÎπÑÏä∑Ìïú Ïπ¥Îìú ÌòïÏãùÏúºÎ°ú Íµ¨ÌòÑ
      default:
        return null;
    }
  };

  return (
    <div className="hmk-form-container">
      <div className="hmk-progress-container">
        <div className="hmk-progress-text">
          <span>ÏûëÏÑ± ÏßÑÌñâÎ•†</span>
          <span>{step} / {totalSteps}</span>
        </div>
        <div className="hmk-progress-bar">
          <div 
            className="hmk-progress-fill"
            style={{width: `${(step / totalSteps) * 100}%`}}
          />
        </div>
      </div>
      
      {renderStep()}
      
      <div className="hmk-navigation-buttons">
        {step > 1 && (
          <button 
            className="hmk-button hmk-prev-button"
            onClick={prevStep}
          >
            <span className="hmk-button-icon">üëà</span>
            Ïù¥Ï†ÑÏúºÎ°ú
          </button>
        )}
        
        {step < totalSteps ? (
          <button 
            className="hmk-button hmk-next-button"
            onClick={nextStep}
          >
            Îã§ÏùåÏúºÎ°ú
            <span className="hmk-button-icon">üëâ</span>
          </button>
        ) : (
          <button 
            className="hmk-button hmk-submit-button"
            onClick={handleSubmit}
          >
            ÏûëÏÑ± ÏôÑÎ£å
            <span className="hmk-button-icon">‚úÖ</span>
          </button>
        )}
      </div>
    </div>
  );
};

export default SeniorJobApplicationForm;
