import React, { useState } from 'react';
import '../asset/css/seniorForm.css';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ResumeTemplate from './ResumeTemplate';

const SeniorJobApplicationForm = () => {
  const [formData, setFormData] = useState({
    // 기본 정보
    name: '',
    title: '',
    residentNumber1: '',
    residentNumber2: '',
    address: '',
    homePhone: '',
    mobilePhone: '',
    familyPhone: '',
    postcode: '',
    detailAddress: '',
    photo: null,
    
    // 최종학력
    education: '',
    
    // 세대구성 형태
    householdType: '',
    
    // 동거가족
    familyMembers: '',
    spouse: '',
    children: '',
    grandchildren: '',
    
    // 일자리사업 참여경력
    workExperience: {
      isNew: false,
      years: []
    },
    
    // 기초생활수급권자
    basicLivingRecipient: '',
    
    // 경제상태
    economicStatus: '',
    
    // 주거형태
    housingType: '',
    
    // 건강상태
    healthStatus: '',
    
    // 참여희망사업
    desiredPrograms: {
      first: '',
      second: '',
      third: ''
    },
    
    // 신청동기
    applicationMotives: [],
    
    // 자원봉사 참여
    volunteerWork: {
      participated: false,
      healthInsurance: ''
    },
    
    // 기초노령연금
    basicPensionStatus: {
      receiving: false,
      amount: ''
    },
    
    // 장기요양
    longTermCare: {
      status: false
    }
  });

  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [photoPreview, setPhotoPreview] = useState(null);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handlePhotoCapture = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, photo: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const nextStep = () => {
    if (validateCurrentStep()) {
      setStep(prev => Math.min(prev + 1, totalSteps));
    } else {
      alert('필수 항목을 입력해주세요.');
    }
  };

  const prevStep = () => {
    setStep(prev => Math.max(prev - 1, 1));
  };

  const validateCurrentStep = () => {
    switch(step) {
      case 1:
        return formData.name && 
               formData.residentNumber1 && 
               formData.residentNumber2 && 
               formData.address && 
               formData.mobilePhone;
      case 2:
        // 학력과 세대구성 형태만 필수로 체크
        return formData.education && formData.householdType;
      case 3:
        return formData.basicLivingRecipient && formData.economicStatus;
      case 4:
        return formData.workExperience.isNew !== undefined;
      case 5:
        // 희망사업 1순위는 필수
        return formData.desiredPrograms.first;
      default:
        return true;
    }
  };

  const handlePostcodeSearch = () => {
    new window.daum.Postcode({
      oncomplete: function(data) {
        // 팝업에서 검색결과 항목을 클릭했을때 실행할 코드를 작성하는 부분입니다.
        setFormData(prev => ({
          ...prev,
          postcode: data.zonecode,
          address: data.address,
          detailAddress: '',
        }));
      }
    }).open();
  };

  const handleFamilyRelationChange = (relation, field, value) => {
    setFormData(prev => ({
      ...prev,
      familyRelations: {
        ...prev.familyRelations,
        [relation]: {
          ...prev.familyRelations[relation],
          [field]: value
        }
      }
    }));
  };

  const renderStep = () => {
    switch(step) {
      case 1:
        return (
          <div className="hmk-form-section">
            <div className="hmk-required-notice">
              * 표시는 필수 입력 항목입니다.
            </div>
            <div className="hmk-table-layout">
              <div className="hmk-photo-cell">
                <div className="hmk-photo-label">사진</div>
                <div className="hmk-photo-upload">
                  {photoPreview ? (
                    <img src={photoPreview} alt="Preview" className="hmk-photo-preview" />
                  ) : (
                    <div className="hmk-photo-placeholder">
                      <span>사진 등록</span>
                    </div>
                  )}
                  <input
                    type="file"
                    accept="image/*"
                    capture="user"
                    onChange={handlePhotoCapture}
                  />
                </div>
              </div>
              
              <div className="hmk-info-cell">
                <div className="hmk-info-row">
                  <div className="hmk-info-label hmk-required">성명</div>
                  <div className="hmk-info-content">
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      className="hmk-input"
                      placeholder="성명을 입력하세요"
                    />
                  </div>
                </div>

                <div className="hmk-info-row">
                  <div className="hmk-info-label hmk-required">주민등록번호</div>
                  <div className="hmk-info-content">
                    <div className="hmk-resident-number-group">
                      <input
                        type="text"
                        name="residentNumber1"
                        maxLength="6"
                        value={formData.residentNumber1}
                        onChange={handleChange}
                        className="hmk-input hmk-resident-first"
                        placeholder="앞 6자리"
                      />
                      <span className="hmk-resident-separator">-</span>
                      <input
                        type="password"
                        name="residentNumber2"
                        maxLength="7"
                        value={formData.residentNumber2}
                        onChange={handleChange}
                        className="hmk-input hmk-resident-second"
                        placeholder="뒤 7자리"
                      />
                      <span className="hmk-age-display">(만 세)</span>
                    </div>
                  </div>
                </div>

                <div className="hmk-info-row">
                  <div className="hmk-info-label hmk-required">주소</div>
                  <div className="hmk-info-content">
                    <div className="hmk-address-group">
                      <div className="hmk-postcode-row">
                        <input
                          type="text"
                          name="postcode"
                          value={formData.postcode}
                          onChange={handleChange}
                          className="hmk-input hmk-postcode"
                          placeholder="우편번호"
                          readOnly
                        />
                        <button 
                          type="button" 
                          className="hmk-button hmk-address-search-btn"
                          onClick={handlePostcodeSearch}
                        >
                          주소 검색
                        </button>
                      </div>
                      <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        className="hmk-input"
                        placeholder="기본주소"
                        readOnly
                      />
                      <input
                        type="text"
                        name="detailAddress"
                        value={formData.detailAddress}
                        onChange={handleChange}
                        className="hmk-input"
                        placeholder="상세주소"
                      />
                    </div>
                  </div>
                </div>

                <div className="hmk-info-row">
                  <div className="hmk-info-label">전화번호</div>
                  <div className="hmk-info-content">
                    <div className="hmk-phone-types">
                      <div className="hmk-phone-type">
                        <span>자택</span>
                        <input
                          type="tel"
                          name="homePhone"
                          value={formData.homePhone}
                          onChange={handleChange}
                          className="hmk-input hmk-phone-input"
                          placeholder="예) 02-1234-5678"
                        />
                      </div>

                      <div className="hmk-phone-type">
                        <span className="hmk-required">휴대폰</span>
                        <input
                          type="tel"
                          name="mobilePhone"
                          value={formData.mobilePhone}
                          onChange={handleChange}
                          className="hmk-input hmk-phone-input"
                          placeholder="예) 010-1234-5678"
                          required
                        />
                      </div>

                      <div className="hmk-phone-type">
                        <span>가족연락처</span>
                        <input
                          type="tel"
                          name="familyPhone"
                          value={formData.familyPhone}
                          onChange={handleChange}
                          className="hmk-input hmk-phone-input"
                          placeholder="예) 010-1234-5678"
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="hmk-form-section">
            <h2>학력 및 가족사항</h2>
            <div className="hmk-form-group">
              <label>최종학력</label>
              <div className="hmk-radio-group">
                {['무학', '초졸', '중졸', '고졸', '전문대졸', '대졸이상'].map((edu) => (
                  <label key={edu} className="hmk-radio-label">
                    <input
                      type="radio"
                      name="education"
                      value={edu}
                      checked={formData.education === edu}
                      onChange={handleChange}
                    />
                    {edu}
                  </label>
                ))}
              </div>
            </div>

            <div className="hmk-form-group">
              <label>세대구성 형태</label>
              <div className="hmk-radio-group">
                {[
                  '독신',
                  '노인부부',
                  '가족동거(노부모,손자녀,장애인)',
                  '가족동거(경제능력가족)',
                  '기타'
                ].map((type) => (
                  <label key={type} className="hmk-radio-label">
                    <input
                      type="radio"
                      name="householdType"
                      value={type}
                      checked={formData.householdType === type}
                      onChange={handleChange}
                    />
                    {type}
                  </label>
                ))}
              </div>
            </div>

            <div className="hmk-form-group">
              <label>동거가족 여부</label>
              <label className="hmk-checkbox-label">
                <input
                  type="checkbox"
                  name="livingWithFamily"
                  checked={formData.livingWithFamily}
                  onChange={handleChange}
                />
                동거가족 있음
              </label>
            </div>

            {formData.livingWithFamily && (
              <div className="hmk-form-group family-details">
                <label>동거가족 관계 및 인원</label>
                <div className="hmk-family-relations">
                  <div className="hmk-relation-item">
                    <label>
                      <input
                        type="checkbox"
                        name="familyRelations.spouse.checked"
                        checked={formData.familyRelations.spouse.checked}
                        onChange={handleNestedChange}
                      />
                      배우자
                    </label>
                    {formData.familyRelations.spouse.checked && (
                      <input
                        type="number"
                        name="familyRelations.spouse.count"
                        value={formData.familyRelations.spouse.count}
                        onChange={handleNestedChange}
                        min="0"
                        className="hmk-count-input"
                      />
                    )}
                  </div>

                  <div className="hmk-relation-item">
                    <label>
                      <input
                        type="checkbox"
                        name="familyRelations.children.checked"
                        checked={formData.familyRelations.children.checked}
                        onChange={handleNestedChange}
                      />
                      자녀
                    </label>
                    {formData.familyRelations.children.checked && (
                      <input
                        type="number"
                        name="familyRelations.children.count"
                        value={formData.familyRelations.children.count}
                        onChange={handleNestedChange}
                        min="0"
                        className="hmk-count-input"
                      />
                    )}
                  </div>

                  <div className="hmk-relation-item">
                    <label>
                      <input
                        type="checkbox"
                        name="familyRelations.relatives.checked"
                        checked={formData.familyRelations.relatives.checked}
                        onChange={handleNestedChange}
                      />
                      친인척
                    </label>
                    {formData.familyRelations.relatives.checked && (
                      <input
                        type="number"
                        name="familyRelations.relatives.count"
                        value={formData.familyRelations.relatives.count}
                        onChange={handleNestedChange}
                        min="0"
                        className="hmk-count-input"
                      />
                    )}
                  </div>

                  <div className="hmk-relation-item">
                    <label>
                      <input
                        type="checkbox"
                        name="familyRelations.other.checked"
                        checked={formData.familyRelations.other.checked}
                        onChange={handleNestedChange}
                      />
                      기타
                    </label>
                    {formData.familyRelations.other.checked && (
                      <div className="hmk-other-relation">
                        <input
                          type="text"
                          name="familyRelations.other.description"
                          value={formData.familyRelations.other.description}
                          onChange={handleNestedChange}
                          placeholder="관계 입력"
                          className="hmk-relation-input"
                        />
                        <input
                          type="number"
                          name="familyRelations.other.count"
                          value={formData.familyRelations.other.count}
                          onChange={handleNestedChange}
                          min="0"
                          className="hmk-count-input"
                        />
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}
          </div>
        );

      case 3:
        return (
          <div className="hmk-form-section">
            <h2>경제 상태</h2>
            <div className="hmk-form-group">
              <label>기초생활수급권자</label>
              <div className="hmk-radio-group">
                <label className="hmk-radio-label">
                  <input
                    type="radio"
                    name="basicLivingRecipient"
                    value="해당"
                    checked={formData.basicLivingRecipient === "해당"}
                    onChange={handleChange}
                  />
                  해당
                </label>
                <label className="hmk-radio-label">
                  <input
                    type="radio"
                    name="basicLivingRecipient"
                    value="비해당"
                    checked={formData.basicLivingRecipient === "비해당"}
                    onChange={handleChange}
                  />
                  비해당 (의료급여 수급권자 2종 포함)
                </label>
              </div>
            </div>

            <div className="hmk-form-group">
              <label>경제상태</label>
              <div className="hmk-radio-group">
                {['매우좋음', '좋음', '보통', '나쁨', '매우나쁨'].map((status) => (
                  <label key={status} className="hmk-radio-label">
                    <input
                      type="radio"
                      name="economicStatus"
                      value={status}
                      checked={formData.economicStatus === status}
                      onChange={handleChange}
                    />
                    {status}
                  </label>
                ))}
              </div>
            </div>

            <div className="hmk-form-group">
              <label>주거형태</label>
              <div className="hmk-radio-group">
                {['자택', '전세', '월세', '기타'].map((type) => (
                  <label key={type} className="hmk-radio-label">
                    <input
                      type="radio"
                      name="housingType"
                      value={type}
                      checked={formData.housingType === type}
                      onChange={handleChange}
                    />
                    {type}
                  </label>
                ))}
              </div>
            </div>

            <div className="hmk-form-group">
              <label>기초노령연금</label>
              <div className="hmk-pension-status">
                <label className="hmk-checkbox-label">
                  <input
                    type="checkbox"
                    name="basicPensionStatus.receiving"
                    checked={formData.basicPensionStatus.receiving}
                    onChange={handleNestedChange}
                  />
                  수급중
                </label>
                {formData.basicPensionStatus.receiving && (
                  <input
                    type="number"
                    name="basicPensionStatus.amount"
                    value={formData.basicPensionStatus.amount}
                    onChange={handleNestedChange}
                    placeholder="금액(만원)"
                  />
                )}
              </div>
            </div>
          </div>
        );

      case 4:
        return (
          <div className="hmk-form-section">
            <h2>참여 경력</h2>
            <div className="hmk-form-group">
              <label>일자리사업 참여경력</label>
              <label className="hmk-checkbox-label">
                <input
                  type="checkbox"
                  name="workExperience.isNew"
                  checked={formData.workExperience.isNew}
                  onChange={handleNestedChange}
                />
                금년도 신규참여
              </label>
              <div className="hmk-checkbox-group">
                {['2012', '2011', '2010', '2009', '2008'].map((year) => (
                  <label key={year} className="hmk-checkbox-label">
                    <input
                      type="checkbox"
                      name="workExperience.years"
                      value={year}
                      checked={formData.workExperience.years.includes(year)}
                      onChange={handleYearsChange}
                    />
                    {year}년
                  </label>
                ))}
              </div>
            </div>
          </div>
        );

      case 5:
        return (
          <div className="hmk-form-section">
            <h2>참여 희망사항</h2>
            <div className="hmk-form-group">
              <label>참여희망사업 (우선순위)</label>
              <div className="hmk-desired-programs">
                <div>
                  <label>1순위</label>
                  <input
                    type="text"
                    name="desiredPrograms.first"
                    value={formData.desiredPrograms.first}
                    onChange={handleNestedChange}
                  />
                </div>
                <div>
                  <label>2순위</label>
                  <input
                    type="text"
                    name="desiredPrograms.second"
                    value={formData.desiredPrograms.second}
                    onChange={handleNestedChange}
                  />
                </div>
                <div>
                  <label>3순위</label>
                  <input
                    type="text"
                    name="desiredPrograms.third"
                    value={formData.desiredPrograms.third}
                    onChange={handleNestedChange}
                  />
                </div>
              </div>
            </div>

            <div className="hmk-form-group">
              <label>신청동기 (복수선택 가능)</label>
              <div className="hmk-checkbox-group">
                {['경제적도움', '자기발전', '사회참여', '시간활용', '건강증진', '기타'].map((motive) => (
                  <label key={motive} className="hmk-checkbox-label">
                    <input
                      type="checkbox"
                      name="applicationMotives"
                      value={motive}
                      checked={formData.applicationMotives.includes(motive)}
                      onChange={handleMotivesChange}
                    />
                    {motive}
                  </label>
                ))}
              </div>
            </div>

            <div className="hmk-form-group">
              <label>자원봉사 참여</label>
              <div className="hmk-volunteer-section">
                <label className="hmk-checkbox-label">
                  <input
                    type="checkbox"
                    name="volunteerWork.participated"
                    checked={formData.volunteerWork.participated}
                    onChange={handleNestedChange}
                  />
                  전년도 참여
                </label>
                <div className="hmk-radio-group">
                  <label>건강보험 직장가입자 해당여부</label>
                  <label className="hmk-radio-label">
                    <input
                      type="radio"
                      name="volunteerWork.healthInsurance"
                      value="해당"
                      checked={formData.volunteerWork.healthInsurance === "해당"}
                      onChange={handleNestedChange}
                    />
                    해당
                  </label>
                  <label className="hmk-radio-label">
                    <input
                      type="radio"
                      name="volunteerWork.healthInsurance"
                      value="비해당"
                      checked={formData.volunteerWork.healthInsurance === "비해당"}
                      onChange={handleNestedChange}
                    />
                    비해당
                  </label>
                </div>
              </div>
            </div>
          </div>
        );

      case 6:
        return (
          <div className="hmk-form-section">
            <h2>입력 내용 확인</h2>
            <div className="hmk-summary-box">
              <h3>기본 정보</h3>
              <p>성명: {formData.name}</p>
              <p>연락처: {formData.phone}</p>
              <p>비상연락처: {formData.emergencyContact}</p>
              
              <h3>가족 사항</h3>
              {formData.livingWithFamily && (
                <div>
                  {Object.entries(formData.familyRelations).map(([relation, data]) => {
                    if (data.checked) {
                      const relationLabel = {
                        spouse: '배우자',
                        children: '자녀',
                        relatives: '친인척',
                        other: '기타'
                      }[relation];
                      return (
                        <p key={relation}>
                          {relationLabel}: {data.count}명
                          {relation === 'other' && ` (${data.description})`}
                        </p>
                      );
                    }
                    return null;
                  })}
                </div>
              )}
              
              {/* ... other summary sections ... */}
            </div>

            {!formData.showPdfPreview ? (
              <button 
                className="hmk-preview-button"
                onClick={() => setFormData(prev => ({ ...prev, showPdfPreview: true }))}
              >
                이력서 미리보기
              </button>
            ) : (
              <div className="hmk-pdf-preview">
                <PDFViewer style={{ width: '100%', height: '500px' }}>
                  <ResumeTemplate formData={formData} />
                </PDFViewer>
                <PDFDownloadLink
                  document={<ResumeTemplate formData={formData} />}
                  fileName="senior-job-application.pdf"
                >
                  {({ loading }) => loading ? '문서 생성중...' : '이력서 다운로드'}
                </PDFDownloadLink>
              </div>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  // 중첩된 객체 처리를 위한 핸들러
  const handleNestedChange = (e) => {
    const { name, value, type, checked } = e.target;
    const [parent, child, subChild] = name.split('.');
    
    setFormData(prev => {
      if (subChild) {
        // 3단계 중첩 객체 (예: familyRelations.spouse.checked)
        return {
          ...prev,
          [parent]: {
            ...prev[parent],
            [child]: {
              ...prev[parent][child],
              [subChild]: type === 'checkbox' ? checked : value
            }
          }
        };
      } else if (child) {
        // 2단계 중첩 객체 (예: basicPensionStatus.receiving)
        return {
          ...prev,
          [parent]: {
            ...prev[parent],
            [child]: type === 'checkbox' ? checked : value
          }
        };
      } else {
        // 1단계 객체
        return {
          ...prev,
          [name]: type === 'checkbox' ? checked : value
        };
      }
    });
  };

  // 연도 선택 핸들러
  const handleYearsChange = (e) => {
    const { value, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      workExperience: {
        ...prev.workExperience,
        years: checked 
          ? [...prev.workExperience.years, value]
          : prev.workExperience.years.filter(year => year !== value)
      }
    }));
  };

  // 신청동기 선택 핸들러
  const handleMotivesChange = (e) => {
    const { value, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      applicationMotives: checked
        ? [...prev.applicationMotives, value]
        : prev.applicationMotives.filter(motive => motive !== value)
    }));
  };

  return (
    <div className="hmk-form-container">
      <div className="hmk-progress-container">
        <div className="hmk-progress-text">
          <span>작성 진행률</span>
          <span>{step} / {totalSteps}</span>
        </div>
        <div className="hmk-progress-bar">
          <div 
            className="hmk-progress-fill"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          />
        </div>
      </div>

      <div className="hmk-form-section">
        {renderStep()}
      </div>

      <div className="hmk-button-container">
        <button
          onClick={prevStep}
          disabled={step === 1}
          className="hmk-button"
        >
          이전
        </button>
        <button
          onClick={nextStep}
          disabled={step === totalSteps}
          className="hmk-button"
        >
          다음
        </button>
      </div>
    </div>
  );
};

export default SeniorJobApplicationForm;
