import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone, faArrowLeft, faCheck, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import '../../assets/css/resumeSequence.css';
import { voiceService } from '../../api/voiceService';
import { useVoiceRecording } from '../../hooks/useVoiceRecording';
import axios from 'axios';

// 미리 녹음된 안내 음성 파일 import
const audioGuides = {
    1: '/static/audio/guides/name_question.mp3',    // "이름이 어떻게 되세요?"
    2: '/static/audio/guides/age_question.mp3',     // "나이가 어떻게 되세요?"
    3: '/static/audio/guides/phone_question.mp3',   // "전화번호가 어떻게 되세요?"
    4: '/static/audio/guides/address_question.mp3'  // "어디 사세요?"
};

const ResumeSequence = () => {
    const navigate = useNavigate();
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        address: '',
        job: ''
    });
    const [isVoiceMode, setIsVoiceMode] = useState(false);
    const [completedSteps, setCompletedSteps] = useState([]);
    const inputRef = useRef(null);
    const sectionsRef = useRef([]);
    const { isRecording, startRecording, stopRecording, checkSilence } = useVoiceRecording();
    const [audioBlob, setAudioBlob] = useState(null);
    const [isProcessing, setIsProcessing] = useState(false);

    // 폼 데이터가 변경될 때마다 로컬 스토리지에 저장
    useEffect(() => {
        localStorage.setItem('resumeFormData', JSON.stringify(formData));
    }, [formData]);

    // 모든 단계가 완료되었는지 확인
    const isAllCompleted = completedSteps.length === 4;

    // 폼 제출 처리
    const handleSubmit = async () => {
        try {
            // 서버로 데이터 전송
            const response = await axios.post('http://localhost:8000/api/voice/resume', formData);
            if (response.data.success) {
                // 성공 시 로컬 스토리지 클리어
                localStorage.removeItem('resumeFormData');
                // 다음 페이지로 이동
                navigate('/success');
            }
        } catch (error) {
            console.error('폼 제출 중 오류:', error);
        }
    };

    // TTS 함수 수정
    const speak = async (step) => {
        try {
            const audioUrl = `http://localhost:8000${audioGuides[step]}`;
            const audio = new Audio(audioUrl);
            
            return new Promise((resolve) => {
                audio.onended = () => resolve();
                audio.onerror = () => {
                    console.error('오디오 재생 중 오류');
                    resolve();
                };
                audio.play();
            });
        } catch (error) {
            console.error('음성 안내 재생 중 오류:', error);
        }
    };

    // 입력 필드 변경 핸들러
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // 음성 입력 시작
    const handleVoiceStart = async (fieldName) => {
        setIsVoiceMode(true);
        try {
            await startRecording();
            checkSilence(async (audioBlob) => {
                const text = await voiceService.speechToText(audioBlob);
                if (text) {
                    setFormData(prev => ({
                        ...prev,
                        [fieldName]: text
                    }));
                }
                setIsVoiceMode(false);
            });
        } catch (error) {
            console.error('음성 입력 에러:', error);
            setIsVoiceMode(false);
        }
    };

    // handleConfirm 함수 수정
    const handleConfirm = async (step) => {
        if (!completedSteps.includes(step)) {
            setCompletedSteps(prev => [...prev, step]);
            setCurrentStep(step + 1);
            
            // 다음 섹션으로 스크롤 - sectionsRef 활용
            setTimeout(() => {
                if (sectionsRef.current[step + 1]) {
                    sectionsRef.current[step + 1].scrollIntoView({
                        behavior: 'smooth',
                        block: 'start',
                        inline: 'nearest'
                    });
                }
            }, 100);

            // 다음 단계의 음성 안내 재생
            if (step + 1 <= 4) {  // 마지막 단계 체크
                await speak(step + 1);
            }
        }
    };

    // 질문 텍스트 가져오기
    const getQuestionForStep = (step) => {
        switch (step) {
            case 1:
                return "이름이 어떻게 되세요?";
            case 2:
                return "나이가 어떻게 되세요?";
            case 3:
                return "전화번호가 어떻게 되세요?";
            case 4:
                return "어디 사세요?";
            default:
                return "";
        }
    };

    // 나이 선택을 위한 버튼 그룹 컴포넌트
    const AgeButtonGroup = ({ value, onChange, disabled }) => (
        <div className="hmk-age-buttons">
            {['50대', '60대', '70대', '80대', '90대 이상'].map((age) => (
                <button
                    key={age}
                    type="button"
                    className={`hmk-age-button ${value === age ? 'selected' : ''}`}
                    onClick={() => onChange('age', age)}
                    disabled={disabled}
                >
                    {age}
                </button>
            ))}
        </div>
    );

    // 각 섹션 컴포넌트
    const Section = ({ step, question, field, value, onChange, onConfirm, isCompleted }) => {
        const [isListening, setIsListening] = useState(false);
        const { isRecording, startRecording } = useVoiceRecording();
        const [inputValue, setInputValue] = useState(value || '');  // 로컬 상태 추가

        // 입력값 변경 핸들러
        const handleChange = (e) => {
            setInputValue(e.target.value);  // 로컬 상태 업데이트
            onChange(e);  // 부모 컴포넌트 상태 업데이트
        };

        // 확인 버튼 핸들러
        const handleConfirm = () => {
            if (inputValue.trim()) {  // 입력값이 있을 때만 확인
                onConfirm(field, inputValue);
            }
        };

        // 음성 입력 핸들러
        const handleVoiceInput = async () => {
            setIsListening(true);
            try {
                await startRecording();
                // ... 음성 입력 로직
            } catch (error) {
                console.error('음성 입력 오류:', error);
            } finally {
                setIsListening(false);
            }
        };

        return (
            <div className={`hmk-sequence-section ${isCompleted ? 'completed' : ''}`}>
                <div className="hmk-sequence-content">
                    <h2 className="hmk-sequence-question">{question}</h2>
                    <div className="hmk-input-container">
                        <input
                            type={field === 'phone' ? 'tel' : 'text'}
                            name={field}
                            className={`hmk-sequence-input ${isListening ? 'listening' : ''}`}
                            value={inputValue}  // 로컬 상태 사용
                            onChange={handleChange}
                            disabled={isListening}
                            placeholder={field === 'phone' ? '전화번호를 입력해주세요' : 
                                       field === 'address' ? '주소를 입력해주세요' : '입력해주세요'}
                        />
                        <button 
                            className={`hmk-voice-button ${isRecording ? 'recording' : ''}`}
                            onClick={handleVoiceInput}
                            disabled={isListening}
                        >
                            <FontAwesomeIcon icon={faMicrophone} />
                        </button>
                    </div>
                    <div className="hmk-confirm-button-container">
                        <button 
                            className={`hmk-confirm-button ${!inputValue.trim() ? 'disabled' : ''}`}
                            onClick={handleConfirm}
                            disabled={!inputValue.trim()}
                        >
                            확인
                        </button>
                    </div>
                </div>
            </div>
        );
    };

    // 컴포넌트 마운트 시 첫 번째 안내 음성 재생
    useEffect(() => {
        localStorage.removeItem('resumeFormData');
        speak(1);  // 첫 번째 질문 재생
        return () => {
            localStorage.removeItem('resumeFormData');
        };
    }, []);

    // 프로그레스바 컴포넌트 수정
    const steps = [
        { label: "이름", step: 1 },
        { label: "나이", step: 2 },
        { label: "전화번호", step: 3 },
        { label: "주소", step: 4 }
    ];

    return (
        <div 
            className="hmk-sequence-container"
            onSubmit={e => e.preventDefault()}
        >
            <div className="hmk-sequence-header">
                <button 
                    className="hmk-back-button"
                    onClick={() => navigate(-1)}
                >
                    <FontAwesomeIcon icon={faArrowLeft} />
                </button>
                <div className="hmk-progress-bar">
                    <div className="hmk-progress-track">
                        <div 
                            className="hmk-progress-fill"
                            style={{ width: `${((completedSteps.length) / (steps.length - 1)) * 100}%` }}
                        ></div>
                    </div>
                    <div className="hmk-progress-steps">
                        {steps.map((step, index) => (
                            <div 
                                key={step.step}
                                className={`hmk-step ${
                                    completedSteps.includes(step.step) ? 'completed' : 
                                    currentStep === step.step ? 'active' : ''
                                }`}
                            >
                                <div className="hmk-step-circle">
                                    {completedSteps.includes(step.step) ? 
                                        <FontAwesomeIcon icon={faCheck} /> : 
                                        step.step
                                    }
                                </div>
                                <span className="hmk-step-label">{step.label}</span>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <div className="hmk-sections-container">
                <Section 
                    step={1}
                    question="이름이 어떻게 되세요?"
                    field="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    onConfirm={handleConfirm}
                    isCompleted={completedSteps.includes(1)}
                />
                {completedSteps.includes(1) && (
                    <Section 
                        step={2}
                        question="나이가 어떻게 되세요?"
                        field="age"
                        value={formData.age}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(2)}
                    />
                )}
                {completedSteps.includes(2) && (
                    <Section 
                        step={3}
                        question="전화번호가 어떻게 되세요?"
                        field="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(3)}
                    />
                )}
                {completedSteps.includes(3) && (
                    <Section 
                        step={4}
                        question="어디 사세요?"
                        field="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        onConfirm={handleConfirm}
                        isCompleted={completedSteps.includes(4)}
                    />
                )}
            </div>

            {/* 모든 단계가 완료되면 제출 버튼 표시 */}
            {isAllCompleted && (
                <div className="hmk-sequence-footer">
                    <button 
                        className="hmk-submit-button"
                        onClick={handleSubmit}
                    >
                        제출하기
                    </button>
                </div>
            )}
        </div>
    );
};

export default ResumeSequence;
