import { PDFDocument, rgb, StandardFonts } from 'pdf-lib';
import { useState } from 'react';

const ResumeTemplate = ({ formData }) => {
  const [pdfUrl, setPdfUrl] = useState(null);

  const fillPdfForm = async () => {
    try {
      // PDF 템플릿 파일 로드
      const formUrl = '/seniorjobhelper.pdf';
      const existingPdfBytes = await fetch(formUrl).then(res => res.arrayBuffer());
      
      // PDF 문서 로드
      const pdfDoc = await PDFDocument.load(existingPdfBytes, { 
        updateMetadata: false // 메타데이터 업데이트 비활성화
      });

      // 첫 페이지 가져오기
      const page = pdfDoc.getPages()[0];
      
      // 폰트 설정
      const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica);
      
      // 기본 텍스트 스타일
      const textOptions = {
        font: helveticaFont,
        size: 12,
        color: rgb(0, 0, 0)
      };

      // 기본 정보
      page.drawText(formData.name, { x: 120, y: 720, ...textOptions });
      page.drawText(`${formData.residentNumber1}-${formData.residentNumber2}`, { x: 120, y: 700, ...textOptions });
      page.drawText(formData.address, { x: 120, y: 680, ...textOptions });
      page.drawText(formData.mobilePhone, { x: 400, y: 720, ...textOptions });

      // 최종학력
      const educationY = 620;
      const educationPositions = {
        '무학': 100,
        '초졸': 180,
        '중졸': 260,
        '고졸': 340,
        '전문대졸': 420,
        '대졸이상': 500
      };
      if (formData.education) {
        page.drawText('■', { x: educationPositions[formData.education], y: educationY, ...textOptions });
      }

      // 세대구성 형태
      const householdY = 580;
      const householdPositions = {
        '독신': 100,
        '가족동거(노부모,손자녀,장애인)': 200,
        '노인부부': 300,
        '가족동거(경제능력가족)': 400,
        '기타': 500
      };
      if (formData.householdType) {
        page.drawText('■', { x: householdPositions[formData.householdType], y: householdY, ...textOptions });
      }

      // 동거가족 정보
      const familyCount = (formData.spouse ? 1 : 0) + 
                         (formData.childrenCount || 0) + 
                         (formData.grandchildrenCount || 0);
      if (familyCount > 0) {
        page.drawText(`${familyCount}명`, { x: 120, y: 540, ...textOptions });
        page.drawText(`(배우자: ${formData.spouse ? '1' : '0'}, 자녀: ${formData.childrenCount || '0'}, 손: ${formData.grandchildrenCount || '0'})`, 
          { x: 180, y: 540, ...textOptions });
      }

      // 일자리사업 참여경력
      const workExpY = 500;
      if (!formData.workExperience.isNew) {
        page.drawText('■', { x: 100, y: workExpY, ...textOptions }); // 금년도 신규참여
      } else {
        formData.workExperience.years.forEach(year => {
          const yearX = 180 + (formData.workExperience.years.indexOf(year) * 80);
          page.drawText('■', { x: yearX, y: workExpY, ...textOptions });
        });
      }

      // 건강상태
      const healthY = 460;
      const healthPositions = {
        '매우건강': 100,
        '건강': 180,
        '보통': 260,
        '나쁨': 340,
        '매우나쁨': 420
      };
      if (formData.healthStatus) {
        page.drawText('■', { x: healthPositions[formData.healthStatus], y: healthY, ...textOptions });
      }

      // 희망 직종
      if (formData.desiredPrograms.first) {
        page.drawText(formData.desiredPrograms.first, { x: 120, y: 420, ...textOptions });
      }
      if (formData.desiredPrograms.second) {
        page.drawText(formData.desiredPrograms.second, { x: 120, y: 400, ...textOptions });
      }
      if (formData.desiredPrograms.third) {
        page.drawText(formData.desiredPrograms.third, { x: 120, y: 380, ...textOptions });
      }

      // 건강보험 직장가입자 여부
      const insuranceY = 340;
      if (formData.volunteerWork.healthInsurance === 'true') {
        page.drawText('■', { x: 100, y: insuranceY, ...textOptions }); // 해당
      } else {
        page.drawText('■', { x: 200, y: insuranceY, ...textOptions }); // 비해당
      }

      // 신청 동기
      if (formData.applicationMotives.length > 0) {
        const motivesY = 300;
        formData.applicationMotives.forEach((motive, index) => {
          page.drawText('■', { x: 100 + (index * 80), y: motivesY, ...textOptions });
        });
      }

      // PDF 저장
      const pdfBytes = await pdfDoc.saveAsBase64({ 
        dataUri: true,
        useObjectStreams: false // 객체 스트림 비활성화
      });

      // 새 창에서 PDF 열기
      window.open(pdfBytes);
      
      // 다운로드용 URL 생성
      const byteCharacters = atob(pdfBytes.split(',')[1]);
      const byteNumbers = new Array(byteCharacters.length);
      for (let i = 0; i < byteCharacters.length; i++) {
        byteNumbers[i] = byteCharacters.charCodeAt(i);
      }
      const byteArray = new Uint8Array(byteNumbers);
      const blob = new Blob([byteArray], { type: 'application/pdf' });
      const url = URL.createObjectURL(blob);
      
      setPdfUrl(url);
    } catch (error) {
      console.error('PDF 생성 중 오류 발생:', error);
      alert('PDF 생성 중 오류가 발생했습니다.');
    }
  };

  const handleDownload = () => {
    if (pdfUrl) {
      const link = document.createElement('a');
      link.href = pdfUrl;
      link.download = `${formData.name}_노인일자리신청서.pdf`;
      link.click();
      
      // URL 정리
      URL.revokeObjectURL(pdfUrl);
      setPdfUrl(null);
    }
  };

  return (
    <div className="pdf-container">
      <button onClick={fillPdfForm} className="hmk-button hmk-preview-button">
        신청서 미리보기
      </button>
      
      {pdfUrl && (
        <div className="pdf-viewer">
          <button onClick={handleDownload} className="hmk-button hmk-download-button">
            PDF 다운로드
          </button>
        </div>
      )}
    </div>
  );
};

export default ResumeTemplate;
